// Katydid - Realistic style, medium complexity
// Designed for 3D printing, approximately 70mm long

$fn = 32;

// Main parameters
body_length = 40;
body_height = 15;
body_width = 12;

// Head parameters
head_length = 12;
head_height = 10;
head_width = 10;

// Leg parameters
leg_thickness = 2;
front_leg_length = 25;
middle_leg_length = 30;
hind_leg_length = 35;

// Wing parameters
wing_length = 35;
wing_width = 15;

// Antenna parameters
antenna_length = 30;
antenna_thickness = 1;

module katydid() {
    // Body
    union() {
        // Main thorax
        color("green")
        translate([0, 0, body_height/2])
        scale([1.2, 0.8, 1])
        sphere(d=body_height);

        // Abdomen
        color("darkgreen")
        translate([-body_length/2, 0, body_height/2])
        scale([1.5, 1, 0.8])
        sphere(d=body_height);

        // Head
        color("green")
        translate([body_length/2 + head_length/2, 0, body_height/2])
        scale([1, 1, 1.1])
        sphere(d=head_height);

        // Eyes
        color("black")
        translate([body_length/2 + head_length/2 + 2, head_width/3, body_height/2])
        sphere(d=4);

        color("black")
        translate([body_length/2 + head_length/2 + 2, -head_width/3, body_height/2])
        sphere(d=4);
    }

    // Antennae
    color("darkgreen")
    translate([body_length/2 + head_length/2 + 2, head_width/3, body_height/2 + 2])
    rotate([0, 30, 20])
    cylinder(h=antenna_length, r=antenna_thickness);

    color("darkgreen")
    translate([body_length/2 + head_length/2 + 2, -head_width/3, body_height/2 + 2])
    rotate([0, 30, -20])
    cylinder(h=antenna_length, r=antenna_thickness);

    // Wings (elytra)
    color("lime")
    translate([-body_length/4, 0, body_height/2 + 3])
    rotate([0, 0, -10])
    scale([1.2, 0.3, 1])
    cube([wing_length, wing_width, 2]);

    // Legs
    module leg(length, angle, z_offset) {
        color("green")
        translate([body_length/4, 0, z_offset])
        rotate([0, 0, angle])
        cylinder(h=length/3, r=leg_thickness);

        translate([body_length/4 + length/3, 0, z_offset])
        rotate([0, 45, angle])
        cylinder(h=length/3, r=leg_thickness);

        translate([body_length/4 + length/3 + length/3*cos(45), 0, z_offset - length/3*sin(45)])
        rotate([0, 90, angle])
        cylinder(h=length/3, r=leg_thickness);
    }

    // Front legs
    leg(front_leg_length, 30, body_height/2 - 2);
    leg(front_leg_length, -30, body_height/2 - 2);

    // Middle legs
    leg(middle_leg_length, 45, body_height/2 - 4);
    leg(middle_leg_length, -45, body_height/2 - 4);

    // Hind legs
    leg(hind_leg_length, 60, body_height/2 - 6);
    leg(hind_leg_length, -60, body_height/2 - 6);
}

// Main assembly
katydid();

