// Herring model - medium complexity realistic style
// Total length ~80mm, height ~20mm, width ~15mm

$fn = 24; // Smooth curves

module herring_body() {
    // Main body - tapered ellipsoid
    scale([1, 0.6, 0.3])
    sphere(d = 60);

    // Tail fin - two angled plates
    translate([30, 0, 0])
    rotate([0, 90, 0])
    scale([0.3, 1, 1])
    cylinder(h = 20, d1 = 25, d2 = 0);

    mirror([0, 1, 0])
    translate([30, 0, 0])
    rotate([0, 90, 0])
    scale([0.3, 1, 1])
    cylinder(h = 20, d1 = 25, d2 = 0);
}

module dorsal_fin() {
    // Main dorsal fin - triangular shape
    translate([-20, 0, 8])
    rotate([0, 10, 0])
    linear_extrude(height = 0.5)
    polygon(points = [[0, 0], [15, 8], [30, 0]]);

    // Fin rays
    for(i = [0:3]) {
        translate([-15 + i*10, 0, 8.5])
        rotate([0, 10 + i*2, 0])
        scale([1, 0.2, 1])
        cylinder(h = 5 + i*2, d = 1);
    }
}

module pectoral_fin(side) {
    // Side fin - positioned behind gills
    translate([-10, side*8, -2])
    rotate([0, 30, side*20])
    scale([1.2, 0.3, 1])
    sphere(d = 12);
}

module pelvic_fin(side) {
    // Belly fins
    translate([5, side*5, -6])
    rotate([20, 0, side*30])
    scale([0.8, 0.3, 1])
    sphere(d = 8);
}

module anal_fin() {
    // Bottom fin near tail
    translate([15, 0, -7])
    rotate([30, 0, 0])
    linear_extrude(height = 0.5)
    polygon(points = [[0, 0], [10, 5], [20, 0]]);
}

module gill_cover() {
    // Gill plate
    translate([-25, 0, -3])
    rotate([0, 0, -10])
    scale([1, 1, 0.5])
    sphere(d = 8);

    // Gill slit
    translate([-28, 0, -2])
    rotate([0, 0, -15])
    scale([0.1, 3, 0.5])
    cube([5, 2, 1]);
}

module mouth() {
    // Protruding upper jaw
    translate([-30, 0, -2])
    rotate([0, 20, 0])
    scale([1, 0.7, 0.3])
    sphere(d = 8);

    // Lower jaw
    translate([-28, 0, -5])
    rotate([0, -20, 0])
    scale([0.9, 0.6, 0.2])
    sphere(d = 7);
}

module eye() {
    // Eye socket
    translate([-28, 5, 3])
    sphere(d = 4);

    // Eye ball
    translate([-29, 5.5, 3.5])
    scale([0.8, 0.8, 0.8])
    sphere(d = 3);
}

module scales_pattern() {
    // Scales along body - simplified as raised ovals
    for(pos = [-25:5:25]) {
        for(height = [-1, 1]) {
            translate([pos, height*3, 0])
            rotate([0, 0, 45])
            scale([1, 0.6, 0.2])
            sphere(d = 2);
        }
    }
}

module herring() {
    union() {
        // Main body parts
        herring_body();

        // Fins
        dorsal_fin();
        pectoral_fin(1);
        pectoral_fin(-1);
        pelvic_fin(1);
        pelvic_fin(-1);
        anal_fin();

        // Head features
        gill_cover();
        mouth();
        eye();
        mirror([0, 1, 0]) eye();

        // Surface details
        scales_pattern();
    }
}

// Generate the herring
herring();

