// Kingfisher OpenSCAD Model - Realistic Medium Complexity
// Size: ~80mm length

$fn = 64;
body_length = 80;
wing_width = 30;
body_height = 25;
head_diameter = 18;
beak_length = 28;

module body() {
    // Main body - elongated ellipsoid
    scale([1.6, 1, 1])
    rotate([0, 90, 0])
    cylinder(h = body_length * 0.7, r1 = body_height/2, r2 = body_height/3);

    // Tail section
    translate([body_length * 0.3, 0, 0])
    rotate([0, 90, 0])
    cylinder(h = body_length * 0.2, r1 = body_height/3, r2 = body_height/6);
}

module head() {
    // Head sphere
    translate([-body_length * 0.35, 0, body_height * 0.3])
    sphere(d = head_diameter);

    // Crest feathers
    for(i = [0:2]) {
        rotate([0, 0, i * 30 - 30])
        translate([-body_length * 0.35, 0, body_height * 0.3 + head_diameter/2])
        rotate([90, 0, 0])
        cylinder(h = 8, r1 = 2, r2 = 1);
    }
}

module beak() {
    // Upper beak
    translate([-body_length * 0.45, 0, body_height * 0.3])
    rotate([0, 90, 0])
    cylinder(h = beak_length, r1 = head_diameter/8, r2 = 0.5);

    // Lower beak
    translate([-body_length * 0.44, 0, body_height * 0.25])
    rotate([0, 90, 0])
    cylinder(h = beak_length * 0.9, r1 = head_diameter/10, r2 = 0.3);
}

module wings() {
    // Left wing
    translate([0, body_height/2, body_height * 0.1])
    rotate([0, 0, 30])
    scale([1, 2, 0.2])
    sphere(d = wing_width);

    // Right wing
    translate([0, -body_height/2, body_height * 0.1])
    rotate([0, 0, -30])
    scale([1, 2, 0.2])
    sphere(d = wing_width);

    // Wing detail
