        // Left wing feathers
        translate([-i * 5, body_height/2 + 5 + i*2, body_height * 0.05])
        rotate([0, 20 - i*5, 0])
        cube([8, 1, 3]);

        // Right wing feathers
        translate([-i * 5, -body_height/2 - 5 - i*2, body_height * 0.05])
        rotate([0, 20 - i*5, 0])
        cube([8, 1, 3]);
    }
}

module tail() {
