// Overall size: ~80mm length, 30mm height, 30mm width

// Global settings
$fn = 32;

// Main module
module ibis() {
    // Body
    body();

    // Head and beak
    translate([30, 0, 10]) rotate([0, -15, 0]) head();

    // Neck
    translate([20, 0, 5]) neck();

    // Legs
    translate([5, -6, -15]) leg();
    translate([5, 6, -15]) mirror([0,1,0]) leg();

    // Wings
    translate([0, -8, 5]) wing();
    translate([0, 8, 5]) mirror([0,1,0]) wing();

    // Tail
    translate([-25, 0, 8]) tail();
}

// Body - oval shaped
module body() {
    // Main body
    difference() {
        union() {
            scale([1.5, 0.8, 0.6]) sphere(r=12);
            translate([5, 0, 0]) cube([20, 20, 15], center=true);
        }
        // Hollow for printing
        scale([1.3, 0.6, 0.4]) sphere(r=12);
    }
}

// Head with long curved beak
module head() {
    union() {
        // Head
        sphere(r=5);

        // Beak - curved
        translate([0, 0, -3]) rotate([0, -20, 0]) 
            scale([1, 0.3, 0.3]) cylinder(h=20, r1=2, r2=0.5);

        // Eye
        translate([2, 3, 2]) sphere(r=1);
        translate([2, 3, 2]) scale([0.5, 0.5, 0.5]) sphere(r=1);
    }
}

// Neck - curved connector
module neck() {
    hull() {
        translate([0, 0, 0]) sphere(r=3);
        translate([10, 0, 5]) sphere(r=3);
    }
}

// Leg with foot
module leg() {
    union() {
        // Upper leg
        translate([0, 0, 7.5]) rotate([0, 15, 0])
            cylinder(h=15, r=1.5);

        // Lower leg
        translate([0, 0, 0]) rotate([0, -15, 0])
            cylinder(h=15, r=1.2);

        // Foot
        translate([0, 0, -1]) foot();
    }
}

// Webbed foot
module foot() {
    // Main foot
    translate([0, 0, -2]) cube([8, 3, 1], center=true);

    // Toes
    for(i = [-1:1]) {
        translate([i*2.5, 0, -2]) cube([1, 0.5, 1], center=true);
    }
}

// Wing with feather details
module wing() {
    difference() {
        union() {
            // Main wing
            translate([0, 0, 0]) scale([1.2, 0.3, 0.8]) sphere(r=10);

            // Primary feathers
            for(i = [0:4]) {
                rotate([0, 0, -i*15]) 
                    translate([5-i, 0, -i*2]) 
                    scale([0.8, 0.1, 0.3]) 
                    sphere(r=8-i);
            }
        }

        // Hollow for printing
        translate([0, 0, 0]) scale([1, 0.2, 0.6]) sphere(r=10);
    }
}

// Tail feathers
module tail() {
    // Tail base
    translate([0, 0, 0]) scale([0.5, 0.2, 0.5]) sphere(r=8);

    // Tail feathers
    for(i = [-2:2]) {
        rotate([0, 0, i*15]) 
            translate([-3, 0, i*2]) 
            scale([1, 0.1, 0.3]) 
            cylinder(h=8, r1=3, r2=1);
    }
}

// Generate the ibis
ibis();

