// Classic Diamond Kite – medium-complexity, fully 3-D printable
// Overall size ≈ 80 mm

$fa = 2; $fs = 0.5;

/* ---------- PARAMETERS ---------- */
wing_span      = 70;      // mm
center_height  = 80;
spine_thick    = 2.5;     // wall / spar thickness
skin_thick     = 1.2;     // sail material
bridle_len     = 40;      // flying line attachment
cross_spar_len = 55;

/* ---------- MAIN KITE ---------- */
module kite_assembly() {
    // Sail (diamond outline, extruded thin shell)
    linear_extrude(height = skin_thick, center = true)
    polygon(points = [
        [0, center_height/2],
        [wing_span/2, 0],
        [0, -center_height/2],
        [-wing_span/2, 0]
    ]);

    // Spine spar (vertical)
    translate([0, 0, 0])
        rotate([90, 0, 0])
        cylinder(h = center_height, d = spine_thick, $fn = 8);

    // Cross spar (horizontal)
    translate([0, 0, 0])
        rotate([0, 90, 0])
        cylinder(h = cross_spar_len, d = spine_thick, $fn = 8);

    // Reinforcement caps at spar ends
    for (sign = [-1, 1]) {
        translate([0, sign * center_height/2, 0])
            rotate([90, 0, 0])
            cylinder(h = spine_thick*2, d1 = spine_thick*1.8, d2 = spine_thick, $fn = 8);
        translate([sign * wing_span/2, 0, 0])
            rotate([0, 90, 0])
            cylinder(h = spine_thick*2, d1 = spine_thick*1.8, d2 = spine_thick, $fn = 8);
    }

    // Bridle attachment loop
    translate([0, -center_height/2 + 5, skin_thick/2 + 1])
        rotate([0, 90, 0])
        torus(ro = 2, ri = 0.8);
}

// Simple torus for the loop
module torus(ro, ri) {
    rotate_extrude($fn = 32)
    translate([ro, 0, 0])
    circle(ri, $fn = 16);
}

/* ---------- TAIL RIBBONS ---------- */
module tail() {
    color("red")
    for (i = [1:6]) {
        translate([0, -center_height/2 - i*9, 0])
        rotate([0, 0, (i % 2 == 0) ? 8 : -8])
        cube([5, 12, 0.6], center = true);
    }
}

/* ---------- ASSEMBLY ---------- */
union() {
    kite_assembly();
    translate([0, 0, -skin_thick/2 - 0.3])  // offset tail slightly
    tail();
}

