// Krill (Euphausiid) - Realistic medium-complexity model
// Body length ≈ 80 mm, printable without supports

$fn = 36;

// Main module
module krill() {
    color("orange") {
        union() {
            cephalothorax();
            abdomen();
            tail_fan();
            antennae();
            legs();
            pleopods();
        }
    }
}

// Cephalothorax (head + fused thorax)
module cephalothorax() {
    // Main carapace shell
    difference() {
        scale([1, 0.9, 0.7])
            sphere(d = 14);
        // Eye sockets
        translate([-5, 6.5, 2])
            rotate([0, 20, 0])
            sphere(d = 4);
        translate([5, 6.5, 2])
            rotate([0, -20, 0])
            sphere(d = 4);
    }

    // Eyes
    translate([-5, 6.3, 2])
        sphere(d = 3);
    translate([5, 6.3, 2])
        sphere(d = 3);

    // Rostrum (beak-like extension)
    translate([0, 8, 0])
        rotate([30, 0, 0])
        cylinder(h = 8, d1 = 3, d2 = 0);
}

// Abdomen (6 segments)
module abdomen() {
    for(i = [1:6]) {
        translate([0, -8 - i*6, -1.5 + i*0.5])
            scale([1 - i*0.05, 1 - i*0.08, 0.8])
            sphere(d = 12 - i);
    }
}

// Tail fan (uropods + telson)
module tail_fan() {
    // Telson (central tail)
    translate([0, -50, 2])
        rotate([30, 0, 0])
        cylinder(h = 12, d1 = 6, d2 = 0);

    // Uropods (lateral tail fins)
    for(side = [-1, 1]) {
        translate([side*5, -45, 0])
            rotate([0, 0, side*20])
            scale([0.3, 1.5, 1])
            sphere(d = 8);
    }
}

// Antennae (antennules + antennae)
module antennae() {
    // First antennae (antennules)
    for(side = [-1, 1]) {
        translate([side*3, 7, 1])
            rotate([0, 0, side*40])
            cylinder(h = 16, d1 = 1, d2 = 0.5);
    }

    // Second antennae
    for(side = [-1, 1]) {
        translate([side*4, 5, 0])
            rotate([0, 0, side*60])
            cylinder(h = 20, d1 = 1.2, d2 = 0.5);
    }
}

// Thoracopods (swimming legs)
module legs() {
    for(i = [0:5]) {
        angle = -45 - i*15;
        length = 8 + i*2;

        translate([sin(i*25)*4, 2 - i*3, -3])
            rotate([90, 0, angle])
            cylinder(h = length, d = 1.2);
    }
}

// Pleopods (abdominal swimming appendages)
module pleopods() {
    for(seg = [0:4]) {
        for(side = [-1, 1]) {
            translate([side*3, -20 - seg*5, -2])
                rotate([0, side*30, 0])
                scale([0.5, 2, 0.2])
                sphere(d = 4);
        }
    }
}

// Render the complete krill
krill();

