// Koi fish model - realistic style with medium complexity
// Size: ~80mm long, 3D printable

$fn = 24;

// Main body module
module koi_body() {
    // Body - elongated ellipsoid
    scale([1.5, 1, 0.8]) 
        sphere(r = 15);

    // Head - slightly flatter sphere
    translate([22, 0, 0])
        scale([1.2, 1, 0.7])
            sphere(r = 12);

    // Tail fin - triangular shape
    translate([-30, 0, 0])
        rotate([0, 0, 90])
            scale([1, 0.3, 0.1])
                cylinder(h = 30, r1 = 15, r2 = 0);

    // Dorsal fin
    translate([0, 0, 12])
        rotate([0, 90, 0])
            scale([0.8, 0.3, 1])
                cylinder(h = 25, r1 = 8, r2 = 0);

    // Pectoral fins - left and right
    pectoral_fin("left");
    pectoral_fin("right");

    // Anal fin
    translate([-15, 0, -10])
        rotate([0, -90, 0])
            scale([0.6, 0.2, 0.8])
                cylinder(h = 20, r1 = 6, r2 = 0);
}

// Pectoral fin module
module pectoral_fin(side) {
    side_factor = (side == "left") ? 1 : -1;

    translate([10, side_factor * 12, -2])
        rotate([0, -20, side_factor * 45])
            scale([0.8, 0.3, 0.1])
                cylinder(h = 18, r1 = 7, r2 = 1);
}

// Eye module
module eye(side) {
    side_factor = (side == "left") ? 1 : -1;

    // Eye socket
    translate([28, side_factor * 8, 3])
        difference() {
            sphere(r = 3);
            translate([1, 0, 0])
                sphere(r = 2.8);
        }

    // Eye ball
    translate([28.5, side_factor * 8, 3])
        sphere(r = 1.5);

    // Pupil
    translate([29.5, side_factor * 8, 3])
        sphere(r = 0.7);
}

// Mouth detail
module mouth() {
    // Upper lip
    translate([32, 0, -2])
        scale([1, 0.8, 0.3])
            sphere(r = 4);

    // Lower lip
    translate([32, 0, -5])
        scale([1, 0.8, 0.3])
            sphere(r = 3.5);
}

// Barbel whiskers
module barbel(side) {
    side_factor = (side == "left") ? 1 : -1;

    for(i = [0:1]) {
        translate([30, side_factor * (6 + i * 2), -4 - i * 2])
            rotate([0, 0, side_factor * -30])
                cylinder(h = 8, r = 0.5);
    }
}

// Scale pattern
module scales() {
    for(i = [0:3]) {
        for(j = [0:5]) {
            translate([20 - j * 8, -15 + i * 8, 9])
                rotate([90, 0, 0])
                    scale([0.8, 0.6, 0.3])
                        cylinder(h = 2, r = 2);
        }
    }
}

// Main assembly
union() {
    koi_body();

    // Eyes
    eye("left");
    eye("right");

    // Mouth
    mouth();

    // Barbel whiskers
    barbel("left");
    barbel("right");

    // Scale pattern
    scales();
}

