// Realistic 3D-Printable Lamb – Medium Complexity
// Size: ~70 mm long, 50 mm tall, 40 mm wide
// All parts are merged into one solid shell for easy 3D printing

$fn = 32; // Smooth curves

module wool_ball(r=4, h=6){
    // Fluffy wool tuft made from stacked spheres
    union(){
        sphere(r);
        translate([0,0,h*0.4]) sphere(r*0.8);
        translate([0,0,h*0.7]) sphere(r*0.6);
    }
}

module leg(side=1){
    // Cylindrical leg with rounded hoof
    translate([side*6,0,0]){
        union(){
            cylinder(h=18, d1=8, d2=6, center=false);
            translate([0,0,18]) cylinder(h=4, d1=6, d2=7, center=false);
        }
    }
}

module ear(){
    // Ear as flattened cylinder
    rotate([0,-20,0])
    scale([1,0.4,0.8])
    cylinder(h=12, d=10, center=true);
}

module head(){
    union(){
        // Main head sphere
        sphere(14);
        // Snout
        translate([12,0,-2])
        scale([1.6,1,1])
        sphere(7);
        // Eyes
        for(x=[-1,1]){
            translate([8, x*5, 3])
            color("black")
            sphere(2);
        }
        // Ears
        translate([-3, 8, 8])
        ear();
        translate([-3, -8, 8])
        mirror([0,1,0])
        ear();
    }
}

module body(){
    union(){
        // Torso ellipsoid
        scale([1.4,1,0.9])
        sphere(20);

        // Add wool tufts across back
        for(i=[-1:0.5:1])
            translate([i*12, 0, 18])
            wool_ball(r=6, h=8);

        for(i=[-0.8:0.4:0.8])
            translate([i*10, 12, 16])
            wool_ball(r=5, h=6);

        for(i=[-0.8:0.4:0.8])
            translate([i*10, -12, 16])
            wool_ball(r=5, h=6);
    }
}

