        scale([1.2, 1, 0.8])
        sphere(d=6, $fn=16);

        // Thorax
        translate([0, 0, 5])
        scale([1.5, 1.2, 2])
        sphere(d=8, $fn=16);

        // Abdomen
        translate([0, 0, -5])
        scale([1.8, 1.4, 3.5])
        sphere(d=12, $fn=16);
    }

    // Eyes (compound)
    for(side = [-1, 1]) {
        color("black")
        translate([side * 3, 2, 10])
        rotate([0, 20 * side, 0])
        sphere(d=3, $fn=8);
    }

    // Antennae
    module antenna() {
        color("brown")
        translate([0, 0, 0])
        rotate([0, 0, 0])
        cylinder(h=20, d1=0.5, d2=0.2, $fn=8);

        // Antennal segments
        for(i = [1:4]) {
            translate([0, 0, i * 5])
            sphere(d=1.2, $fn=8);
        }
    }

    // Left antenna
    translate([-2.5, 1.5, 12])
    rotate([0, 20, -30])
    antenna();

    // Right antenna
    translate([2.5, 1.5, 12])
    mirror([1, 0, 0])
    rotate([0, 20, -30])
    antenna();

    // Wings - four wings with venation
    module wing() {
        color("transparent", 0.3)
        difference() {
            // Wing membrane
            scale([1, 0.6, 0.1])
            sphere(d=40, $fn=32);

            // Wing base cut
            translate([0, -15, 0])
            cube([15, 20, 2], center=true);

            // Wing tip taper
            translate([25, 0, 0])
            rotate([0, 90, 0])
            cylinder(h=30, d1=0, d2=20, $fn=16);
        }

        // Wing venation (raised lines)
        color("lightgreen")
        for(i = [0:3]) {
            translate([i * 8 - 12, -3, 0.2])
            rotate([0, 0, i * 5])
            cube([12, 0.4, 0.4]);
        }

        for(i = [0:2]) {
            translate([i * 10 - 10, 3, 0.2])
            rotate([0, 0, -i * 8])
            cube([10, 0.4, 0.4]);
        }
    }

    // Forewings
    translate([0, 0, 8])
    rotate([0, 0, 45])
    wing();

    translate([0, 0, 8])
    rotate([0, 0, -45])
    mirror([1, 0, 0])
    wing();

    // Hindwings (smaller)
    translate([0, 0, 5])
    rotate([0, 0, 55])
    scale([0.8, 0.8, 1])
    wing();

    translate([0, 0, 5])
    rotate([0, 0, -55])
    mirror([1, 0, 0])
    scale([0.8, 0.8, 1])
    wing();

    // Legs - six legs
    module leg(segment_length = 8) {
        color("green")
        union() {
            // Coxa
            translate([0, 0, 0])
            rotate([90, 0, 0])
            cylinder(h=3, d=1.2, $fn=8);

            // Femur
            translate([0, 0, -3])
            rotate([90, 0, 0])
            cylinder(h=segment_length * 0.4, d=0.8, $fn=8);

            // Tibia
            translate([0, 0, -3 - segment_length * 0.4])
            rotate([90, 0, 0])
            cylinder(h=segment_length * 0.5, d=0.6, $fn=8);

            // Tarsi
            translate([0, 0, -3 - segment_length * 0.9])
            rotate([90, 0, 0])
            cylinder(h=segment_length * 0.3, d1=0.5, d2=0.3, $fn=6);

            // Claws
            translate([0, 0, -3 - segment_length])
            rotate([90, 0, 0])
            for(side = [-1, 1]) {
                translate([side * 0.3, 0, 0])
                cylinder(h=0.8, d1=0.1, d2=0.3, $fn=4);
            }
        }
    }

    // Front legs
    for(side = [-1, 1]) {
        translate([side * 4, 0, 7])
        rotate([0, 0, side * 30])
        leg(7);
    }

    // Middle legs
    for(side = [-1, 1]) {
        translate([side * 5, 0, 3])
        rotate([0, 0, side * 20])
        leg(9);
    }

    // Hind legs
    for(side = [-1, 1]) {
        translate([side * 4, 0, -2])
        rotate([0, 0, side * 15])
        leg(10);
    }
}

