// Realistic Medium-Complexity Lamprey – OpenSCAD
// Overall length ≈ 80 mm, printable without supports

// Basic parameters
$fn = 24;               // Global resolution
body_diameter = 14;
body_length = 60;
tail_taper = 0.4;
head_diameter = 18;
sucker_depth = 8;
teeth_rows = 8;
teeth_per_row = 12;
gill_pouches = 7;
gill_size = 2.5;

// Main body
module lamprey_body() {
    union() {
        // Torso segments
        for (i = [0:5]) {
            hull() {
                translate([i*10, 0, 0])
                    scale([1, 1 - i*0.08, 1 - i*0.08])
                        sphere(d = body_diameter);
                translate([(i+1)*10, 0, 0])
                    scale([1, 1 - (i+1)*0.08, 1 - (i+1)*0.08])
                        sphere(d = body_diameter);
            }
        }

        // Tail taper
        hull() {
            translate([60, 0, 0])
                sphere(d = body_diameter * tail_taper);
            translate([80, 0, 0])
                sphere(d = body_diameter * tail_taper * 0.4);
        }
    }
}

// Oral sucker disc
module sucker() {
    difference() {
        // Disc
        translate([-sucker_depth, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = head_diameter, d = head_diameter, center = true);

        // Central cavity
        translate([-sucker_depth + 2, 0, 0])
            rotate([0, 90, 0])
                cylinder(h = head_diameter + 2, d = head_diameter * 0.7, center = true);
    }
}

// Radial teeth
module teeth() {
    for (j = [0:teeth_rows-1]) {
        for (i = [0:teeth_per_row-1]) {
            rotate([0, 0, i * 360 / teeth_per_row])
                translate([-sucker_depth + 2 + j * 0.8, head_diameter/2 - 2, 0])
                    rotate([90, 0, 0])
                        cylinder(h = 2.5, d1 = 1.2, d2 = 0.6);
        }
    }
}

// Gill (spiracle) pouches
module gills() {
    for (k = [0:gill_pouches-1]) {
        translate([k * 6 + 10, 0, body_diameter/2 - 0.5])
            rotate([90, 0, 0])
                cylinder(h = gill_size * 2, d = gill_size, $fn = 6);
    }
}

// Dorsal fins (low-profile printable ridges)
module dorsal_fins() {
    // First dorsal
    translate([35, 0, body_diameter/2])
        cube([18, body_diameter * 0.8, 2], center = true);

    // Second dorsal
    translate([55, 0, body_diameter/2 * 0.9])
        cube([15, body_diameter * 0.7, 2], center = true);
}

// Assembly
union() {
    lamprey_body();
    color("gray")
        sucker();
    color("ivory")
        teeth();
    color("dimgray")
        gills();
    color("slategray")
        dorsal_fins();
}

