// Linnet - medium complexity realistic style
// Suitable for FDM printing, 100mm length

$fn = 36; // Smooth curves

// Main body
module body() {
    // Torso - slightly flattened ellipsoid
    scale([1, 0.7, 0.5]) sphere(r=20);

    // Tail base
    translate([0, -25, 0]) rotate([0, 0, 0])
        scale([0.8, 0.4, 0.2]) sphere(r=15);

    // Tail feathers - 6 tapered cylinders
    for(i=[-2:2]) {
        translate([i*4, -35, 0])
            rotate([0, 10, i*3])
            scale([0.3, 1, 0.1]) cylinder(h=20, r1=5, r2=2);
    }
}

// Head with realistic proportions
module head() {
    // Main head sphere
    translate([0, 20, 12]) sphere(r=10);

    // Beak - conical
    translate([0, 30, 12])
        rotate([0, 90, 0])
        cylinder(h=12, r1=0.5, r2=2);

    // Crest - small raised feathers
    for(i=[-1:1]) {
        translate([i*3, 15, 16])
            rotate([i*10, 0, 0])
            scale([0.3, 0.3, 1]) cylinder(h=5, r=2);
    }
}

// Wing structure
module wing(side) {
    mirror([side, 0, 0]) {
        // Primary wing section
        translate([-15, 0, 5])
            rotate([0, 0, -20])
            scale([0.15, 1, 0.5])
            cylinder(h=35, r=8);

        // Secondary feathers - layered cylinders
        for(i=[0:3]) {
            translate([-15-i*3, i*5-5, 5+i])
                rotate([0, 0, -30-i*5])
                scale([0.1, 0.8, 0.1])
                cylinder(h=20-i*3, r=6-i);
        }

        // Wing coverts
        translate([-10, 5, 8])
            scale([0.1, 0.6, 0.4])
            sphere(r=8);
    }
}

// Leg and foot
module leg(side) {
    mirror([side, 0, 0]) {
        // Upper leg
        translate([-8, -10, -8])
            rotate([15, 0, 0])
            cylinder(h=8, r=1.5);

        // Lower leg
        translate([-8, -10, -15])
            rotate([45, 0, 0])
            cylinder(h=6, r=1.2);

        // Foot with toes
        translate([-8, -10, -20])
            rotate([90, 0, 0]) {
            // Central toe
            cylinder(h=4, r=0.8);

            // Side toes
            translate([0, 1, 0]) rotate([0, 30, 0])
                cylinder(h=3.5, r=0.6);
            translate([0, -1, 0]) rotate([0, -30, 0])
                cylinder(h=3.5, r=0.6);

            // Back toe
            translate([0, 0, -1.5]) rotate([0, -45, 0])
                cylinder(h=2, r=0.5);
        }
    }
}

// Eye detail
module eye(side) {
    mirror([side, 0, 0]) {
        translate([-5, 23, 14])
            sphere(r=1.5);

        // Pupil
        translate([-6, 23.5, 15])
            sphere(r=0.7);
    }
}

// Chest feather detail
module chest() {
    for(i=[0:5]) {
        for(j=[-3:3]) {
            translate([j*2, 5-i*2, -5+i])
                rotate([0, 0, j*5])
                scale([0.3, 0.3, 0.1])
                sphere(r=3-i*0.3);
        }
    }
}

// Assemble the linnet
union() {
    body();
    head();
    wing(1);   // Left wing
