// Lemming - Realistic Style, Medium Complexity
// Overall size ~60mm, 3D-printable

// Parameters
$fn=32;  // Smooth curves
body_len = 35;
body_rad = 12;
head_rad = 10;
ear_rad = 4;
ear_len = 7;
leg_len = 10;
leg_rad = 4;
tail_len = 20;
tail_rad = 4;
paw_rad = 3;
eye_rad = 1.5;
whisker_len = 8;

// Main module
module lemming(){
    union(){
        // Body
        translate([0,0,leg_len])
        scale([1,0.8,0.7])
        sphere(r=body_rad);

        // Head
        translate([body_rad+head_rad*0.5, 0, leg_len+body_rad*0.7])
        sphere(r=head_rad);

        // Snout
        translate([body_rad+head_rad*1.5, 0, leg_len+body_rad*0.7])
        scale([1.5,1,1])
        sphere(r=head_rad*0.6);

        // Ears
        for(side=[-1,1]){
            translate([body_rad+head_rad*0.2, side*head_rad*0.7, leg_len+body_rad*0.7+head_rad*0.8])
            rotate([0, side*20, 0])
            rotate([0,0,side*10])
            scale([0.7,0.4,1])
            sphere(r=ear_rad);
        }

        // Legs
        for(x=[-body_rad*0.5, body_rad*0.5])
            for(side=[-1,1]){
                translate([x, side*body_rad*0.6, 0])
                rotate([90,0,0])
                cylinder(h=leg_len, r1=leg_rad, r2=paw_rad);
            }

        // Tail
        translate([-body_rad-5, 0, leg_len+body_rad*0.3])
        rotate([0,20,0])
        scale([1,1,0.8])
        cylinder(h=tail_len, r1=tail_rad, r2=tail_rad*0.5);

        // Eyes
        for(side=[-1,1]){
            translate([body_rad+head_rad*1.1, side*head_rad*0.5, leg_len+body_rad*0.7+head_rad*0.2])
            sphere(r=eye_rad);
            translate([body_rad+head_rad*1.1, side*head_rad*0.5, leg_len+body_rad*0.7+head_rad*0.2])
            scale([0.5,0.5,0.2])
            sphere(r=eye_rad);
        }

        // Whiskers (thin cylinders for printability)
        for(side=[-1,1])
            for(angle=[-30,0,30]){
                translate([body_rad+head_rad*1.7, side*head_rad*0.5, leg_len+body_rad*0.7])
                rotate([0, side*angle, 0])
                cylinder(h=whisker_len, r=0.4);
            }

        // Fur texture (small bumps)
        for(i=[0:10:360]){
            for(j=[0:15:45]){
                rotate([0,0,i])
                translate([body_rad*0.8, 0, leg_len+j])
                sphere(r=0.8);
            }
        }
    }
}

// Render
lemming();

