            scale([1 - i*0.08, 1 - i*0.08, 1 - i*0.05])
                rotate([0, 10*i, 0])
                    cylinder(h=4, r1=6-i*0.5, r2=5.5-i*0.5);
    }
}

module lizard_leg(side, angle) {
