// Realistic Louse Model - Medium Complexity
// Designed for 3D printing with 2mm wall thickness

// Overall dimensions: ~60mm length, ~25mm width, ~20mm height

module louse_body() {
    // Main body segments
    // Head - slightly flattened sphere
    translate([0, 0, 5])
        scale([1, 0.8, 0.6])
        sphere(r=6, $fn=20);

    // Thorax - tapered cylinder
    translate([0, 0, -2])
        cylinder(h1=8, h2=10, r1=7, r2=5, $fn=20);

    // Abdomen - elongated oval
    translate([0, 0, -12])
        scale([1.2, 1, 0.8])
        sphere(r=10, $fn=20);
}

module louse_legs() {
    // Six legs in pairs
    for(side = [0, 1]) {
        mirror([side ? 1 : -1, 0, 0]) {
            // Front legs (shorter)
            translate([3, 3, 0])
                rotate([0, 0, -30])
                leg_segment(8, 2);

            // Middle legs (medium)
            translate([0, 4, -4])
                rotate([0, 0, -45])
                leg_segment(10, 2);

            // Rear legs (longer)
            translate([-3, 3, -8])
                rotate([0, 0, -60])
                leg_segment(12, 2);
        }
    }
}

module leg_segment(length, thickness) {
    // Upper leg segment
    cylinder(h=length*0.4, r=thickness, $fn=8);

    // Knee joint
    translate([0, 0, length*0.4])
        sphere(r=thickness*1.2, $fn=8);

    // Lower leg segment
    translate([0, 0, length*0.4])
        rotate([20, 0, 0])
        cylinder(h=length*0.6, r=thickness*0.8, $fn=8);

    // Claw at end
    translate([0, 0, length])
        scale([0.5, 0.5, 1])
        sphere(r=thickness, $fn=8);
}

module louse_antennae() {
    // Pair of antennae
    for(side = [0, 1]) {
        mirror([side ? 1 : -1, 0, 0]) {
            translate([4, 0, 9])
                rotate([0, -30, 0])
                cylinder(h=6, r=0.5, $fn=8);

            // Antenna tip
            translate([9, 0, 7])
                sphere(r=1, $fn=8);
        }
    }
}

module louse_mouthparts() {
    // Proboscis for feeding
    translate([0, 0, 2])
        rotate([90, 0, 0])
        cylinder(h=3, r1=1.5, r2=0.5, $fn=10);

    // Mouth hooks
    for(angle = [0:120:239]) {
        rotate([0, 0, angle])
        translate([2, 0, 1])
            cylinder(h=2, r=0.3, $fn=6);
    }
}

module louse_segments() {
    // Abdominal segments
    for(i = [0:5]) {
        translate([0, 0, -12 - i*2])
            difference() {
                scale([1.2 - i*0.05, 1 - i*0.02, 0.5])
                sphere(r=10 - i*1, $fn=20);

                // Remove bottom half for flat printing
                translate([0, 0, -5])
                cube([30, 30, 10], center=true);
            }
    }
}

// Main assembly
union() {
    // Body structure
    louse_body();

    // Surface details
    louse_segments();

    // Appendages
    louse_legs();
    louse_antennae();
    louse_mouthparts();

    // Small bumps for texture
    for(i = [0:20]) {
        rotate([0, 0, i*36])
        translate([7 + sin(i*5)*2, 0, -5 - i*0.5])
            sphere(r=0.5, $fn=6);
    }
}

// Flat base for printing
