// Loris model - realistic style, medium complexity
// 3D printable with no overhangs

// Overall dimensions: ~80mm length, ~40mm height, ~35mm width

module loris() {
    // Main body - oval shaped torso
    union() {
        // Torso - elongated sphere
        translate([0, 0, 20]) {
            scale([1.2, 0.8, 0.8]) 
            sphere(d=35, $fn=32);
        }

        // Belly - flattened cylinder
        translate([0, 0, 10]) {
            scale([1.3, 1, 0.6])
            cylinder(d=30, h=20, $fn=32);
        }

        // Head - sphere with flat bottom
        translate([-25, 0, 35]) {
            sphere(d=25, $fn=32);
            translate([0, 0, -12]) 
            cylinder(d=25, h=12, $fn=32);
        }

        // Face mask - darker area
        difference() {
            translate([-35, 0, 35]) 
            sphere(d=20, $fn=32);
            translate([-45, 0, 35]) 
            sphere(d=15, $fn=32);
        }

        // Eyes
        for(x = [-5, 5]) {
            translate([-35, x, 38]) {
                color("black")
                sphere(d=6, $fn=16);
                translate([2, 0, 0])
                color("white")
                sphere(d=2, $fn=8);
            }
        }

        // Nose
        translate([-40, 0, 32])
        scale([1, 0.7, 0.5])
        sphere(d=6, $fn=16);

        // Ears - small spheres
        for(y = [-10, 10]) {
            translate([-25, y, 47]) {
                scale([0.8, 0.8, 1.2])
                sphere(d=8, $fn=16);
            }
        }

        // Arms
        for(side = [-1, 1]) {
            // Upper arm
            translate([-5, side * 15, 30]) {
                rotate([0, 30 * side, -30])
                cylinder(d1=10, d2=8, h=20, $fn=16);
            }

            // Lower arm
            translate([-15, side * 25, 20]) {
                rotate([0, 30 * side, 0])
                cylinder(d=8, h=25, $fn=16);
            }

            // Hand
            translate([-15, side * 30, 15]) {
                sphere(d=10, $fn=16);
            }
        }

        // Legs
        for(side = [-1, 1]) {
            // Thigh
            translate([15, side * 12, 25]) {
                rotate([0, 0, -20])
                cylinder(d1=12, d2=10, h=15, $fn=16);
            }

            // Lower leg
            translate([25, side * 15, 15]) {
                rotate([0, 0, -20])
                cylinder(d=10, h=20, $fn=16);
            }

            // Foot
            translate([35, side * 18, 8]) {
                scale([1.2, 0.8, 0.4])
                sphere(d=12, $fn=16);
            }
        }

        // Tail - stubby
        translate([35, 0, 25]) {
            rotate([0, 0, 20])
            cylinder(d1=10, d2=8, h=15, $fn=16);
        }

        // Fur texture - small spheres along back
        for(i = [-20:5:30]) {
            for(j = [-15:10:15]) {
                if(j*j + i*i < 400 && i < 20) {
                    translate([i, j, 25 + sin(i/5) * 5]) {
                        sphere(d=3, $fn=8);
                    }
                }
            }
        }
    }
}

// Render the loris
loris();

