// Loach - Realistic Style, Medium Complexity
// Overall dimensions: ~80mm long, ~20mm tall, ~15mm wide

// Body parameters
body_length = 80;
body_height = 20;
body_width = 15;

// Head parameters
head_length = 15;
head_height = 12;
head_width = 10;

// Tail parameters
tail_length = 20;

// Fin parameters
fin_height = 6;
fin_thickness = 1.5;

// Eye parameters
eye_radius = 2;
eye_offset = 3;

// Barbel parameters
barbel_length = 8;
barbel_radius = 0.5;

// Body segments
segments = 12;

// Main body module
module loach_body() {
    // Create tapered body using hull of spheres
    for(i = [0:segments]) {
        translate([i * (body_length / segments), 0, 0]) {
            // Tapering calculation
            taper = 1 - (i / segments) * 0.7;
            sphere(r = body_width/2 * taper, $fn = 16);
        }
    }

    // Add dorsal ridge
    for(i = [3:segments-2]) {
        translate([i * (body_length / segments), 0, body_height/2 * (1 - i/segments)]) {
            cube([body_length/segments * 0.8, body_width * 0.3, body_height * 0.3], center = true);
        }
    }
}

// Head module
module loach_head() {
    // Main head shape
    translate([-body_length/2 - head_length/2, 0, 0]) {
        // Head sphere
        sphere(r = head_width/2, $fn = 16);

        // Snout extension
        translate([-head_length/2, 0, -head_height/4]) {
            scale([1.5, 0.8, 0.6]) {
                sphere(r = head_width/2, $fn = 16);
            }
        }

        // Mouth opening
        difference() {
            translate([-head_length/2 - 1, 0, -head_height/3]) {
                sphere(r = head_width/2.5, $fn = 16);
            }
            translate([-head_length/2 - 2, 0, -head_height/3]) {
                sphere(r = head_width/2.8, $fn = 16);
            }
        }
    }
}

// Tail module
module loach_tail() {
    // Tail fin
    translate([body_length/2 + tail_length/2, 0, 0]) {
        rotate([0, 0, 90]) {
            linear_extrude(height = fin_thickness) {
                polygon(points = [
                    [0, 0],
                    [tail_length, body_width/3],
                    [tail_length, -body_width/3],
                    [0, 0]
                ]);
            }
        }
    }

    // Tail body taper
    translate([body_length/2 + tail_length/4, 0, 0]) {
        scale([0.3, 0.3, 0.3]) {
            sphere(r = body_width/2, $fn = 16);
        }
    }
}

// Dorsal fin module
module dorsal_fin() {
    for(i = [2:segments-3]) {
        translate([i * (body_length / segments), 0, body_height/2]) {
            rotate([90, 0, 0]) {
                cylinder(h = fin_height * (1 - i/segments), r1 = fin_thickness, r2 = 0.5, $fn = 8);
            }
        }
    }
}

// Pectoral fins module
module pectoral_fins() {
    // Left fin
    translate([-body_length/4, body_width/2 + 1, -body_height/4]) {
        rotate([0, 90, 30]) {
            cylinder(h = fin_height, r1 = fin_thickness * 2, r2 = 0.5, $fn = 8);
        }
    }

    // Right fin
    translate([-body_length/4, -body_width/2 - 1, -body_height/4]) {
        rotate([0, 90, -30]) {
            cylinder(h = fin_height, r1 = fin_thickness * 2, r2 = 0.5, $fn = 8);
        }
    }
}

// Eyes module
module loach_eyes() {
    // Left eye
    translate([-body_length/2 - head_length/2 + eye_offset, head_width/2 - 1, head_height/4]) {
        sphere(r = eye_radius, $fn = 8);
    }

    // Right eye
    translate([-body_length/2 - head_length/2 + eye_offset, -head_width/2 + 1, head_height/4]) {
        sphere(r = eye_radius, $fn = 8);
    }
}

// Barbel (whiskers) module
module loach_barbels() {
    // Top barbels
    for(side = [-1, 1]) {
        translate([-body_length/2 - head_length/2 + 2, side * (head_width/2 - 1), -head_height/3]) {
            rotate([0, -side * 15, side * 30]) {
                cylinder(h = barbel_length, r = barbel_radius, $fn = 8);
            }
        }
    }

    // Bottom barbels
    for(side = [-1, 1]) {
        translate([-body_length/2 - head_length/2 + 1, side * (head_width/3), -head_height/2]) {
            rotate([0, side * 15, side * 20]) {
                cylinder(h = barbel_length * 0.7, r = barbel_radius * 0.8, $fn = 8);
            }
        }
    }
}

// Main assembly
union() {
    // Central positioning
    translate([0, 0, body_height/2]) {
        // Main body parts
        union() {
            loach_body();
            loach_head();
            loach_tail();
        }

        // Fins
        union() {
            dorsal_fin();
            pectoral_fins();
        }

        // Details
        union() {
            loach_eyes();
            loach_barbels();
        }
    }
}

