// Lionfish - Realistic Style, Medium Complexity
// 3D printable, ~80mm long

// Parameters
$fn = 32;
body_length = 40;
body_height = 25;
tail_length = 35;
fin_height = 30;

// Main body
module body() {
    union() {
        // Torso
        translate([0, 0, 0])
            scale([1.3, 1, 1])
            sphere(d=body_height);

        // Head
        translate([body_length/2, 0, 0])
            sphere(d=body_height*0.8);

        // Snout
        translate([body_length/2 + 10, 0, -2])
            rotate([0, 10, 0])
            cylinder(h=10, d1=8, d2=5);
    }
}

// Tail
module tail() {
    translate([-body_length/2, 0, 0])
        rotate([0, -10, 0])
        cylinder(h=tail_length, d1=body_height*0.6, d2=2);
}

// Dorsal spines
module dorsal_spines() {
    for(i = [-15:5:15]) {
        translate([i, 0, body_height/2 + 5])
            rotate([0, -5, 0])
            cylinder(h=fin_height, d1=1.5, d2=0.5);
    }
}

// Pectoral fins
module pectoral_fin(side) {
    translate([body_length/4, side*body_height/2, -5])
        rotate([0, 0, side*30])
        for(i = [0:4]) {
            translate([i*3, 0, 0])
                rotate([0, i*5, 0])
                cylinder(h=15-i, d1=2, d2=1);
        }
}

// Stripes pattern
module stripes() {
    for(i = [-20:5:20]) {
        translate([i, 0, 0])
            scale([1, 1.2, 1])
            difference() {
                sphere(d=body_height*1.1);
                translate([0, 0, -body_height/2])
                    cube([body_height*2, body_height*2, body_height], center=true);
            }
    }
}

// Eyes
module eye(side) {
    translate([body_length/2 + 8, side*body_height/3, body_height/4])
        sphere(d=4);
    translate([body_length/2 + 9.5, side*body_height/3, body_height/4])
        sphere(d=2);
}

