// Macaque Monkey - Realistic Style, Medium Complexity
// Overall size ~70mm tall

// Parameters
$fn = 32;
body_width = 25;
body_height = 30;
body_depth = 18;
head_size = 18;
limb_radius = 4;
limb_length = 22;
tail_length = 35;
tail_radius = 3;

module macaque() {
    // Main body
    union() {
        // Torso - slightly tapered
        translate([0, 0, 20])
        scale([1, 1.2, 1.3])
        sphere(d=body_width);

        // Abdomen
        translate([0, 0, 10])
        cylinder(h=15, d1=body_width-2, d2=body_width-4);

        // Chest
        translate([0, -2, 25])
        sphere(d=body_width-3);
    }

    // Head
    translate([0, 0, 42])
    union() {
        // Skull
        sphere(d=head_size);

        // Muzzle
        translate([0, -9, -2])
        scale([1.2, 1.5, 1])
        sphere(d=head_size-6);

        // Nose
        translate([0, -13, 0])
        rotate([90, 0, 0])
        cylinder(h=3, d1=4, d2=2);

        // Eyes
        for(x=[-4, 4]) {
            translate([x, -4, 2])
            sphere(d=3);
            translate([x, -5, 2.5])
            sphere(d=1.5);
        }

        // Ears
        for(x=[-8, 8]) {
            translate([x, 0, 0])
            rotate([0, 15 * (x > 0 ? -1 : 1), 0])
            scale([0.7, 0.3, 1])
            sphere(d=6);
        }
    }

    // Arms
    module arm(side) {
        translate([side * (body_width/2 - 2), 0, 30])
        rotate([0, 0, side * 20])
        rotate([90, 0, 0])
        cylinder(h=limb_length, r=limb_radius);

        // Hand
        translate([side * (body_width/2 - 2 + limb_length/2), side * 5, 15])
        sphere(d=limb_radius * 1.5);
    }

    arm(1);
    arm(-1);

    // Legs
    module leg(side) {
        translate([side * (body_width/3), 0, 10])
        rotate([0, 0, side * -10])
        rotate([90, 0, 0])
        cylinder(h=limb_length, r=limb_radius + 1);

        // Foot
        translate([side * (body_width/3 + 2), side * 5, 0])
        scale([1.5, 2, 0.8])
        sphere(d=limb_radius * 2);
    }

    leg(1);
    leg(-1);

    // Tail - curved
    for(i=[0:5]) {
        rotate([0, 0, -20 * sin(i * 30)])
        translate([0, -body_depth/2 - i * 5, 10 + i * 3])
        rotate([90, 0, 0])
        cylinder(h=tail_length/6, r=tail_radius * (1 - i * 0.1));
    }
}

// Generate the macaque
macaque();

