// Overall dimensions: ~80mm length, ~40mm height, ~30mm width

$fn = 32;  // Smooth curves for printing

// Main module
module mantis() {
    // Body assembly
    union() {
        // Head
        translate([0, 0, 35]) rotate([0, 0, 0]) head();

        // Thorax
        translate([0, 0, 25]) thorax();

        // Abdomen
        translate([0, 0, 15]) abdomen();

        // Front legs (raptorial)
        translate([8, 0, 30]) rotate([0, -20, 30]) raptorial_leg();
        mirror([1, 0, 0]) translate([8, 0, 30]) rotate([0, -20, -30]) raptorial_leg();

        // Middle legs
        translate([5, 0, 22]) rotate([0, 0, 45]) middle_leg();
        mirror([1, 0, 0]) translate([5, 0, 22]) rotate([0, 0, -45]) middle_leg();

        // Hind legs
        translate([2, 0, 18]) rotate([0, 0, 60]) hind_leg();
        mirror([1, 0, 0]) translate([2, 0, 18]) rotate([0, 0, -60]) hind_leg();

        // Antennae
        translate([2, 2, 40]) rotate([0, 30, 10]) antenna();
        mirror([1, 0, 0]) translate([2, 2, 40]) rotate([0, 30, -10]) antenna();
    }
}

// Head module with compound eyes
module head() {
    union() {
        // Main head capsule
        rotate([90, 0, 0]) cylinder(h=12, r1=6, r2=4);

        // Compound eyes (bulging spheres)
        translate([3, 3, 0]) sphere(r=2.5);
        translate([-3, 3, 0]) sphere(r=2.5);

        // Mandibles (triangular)
        translate([1.5, -2, -2]) rotate([0, -30, 0]) 
            cube([2, 1, 3], center=true);
        translate([-1.5, -2, -2]) rotate([0, -30, 0]) 
            cube([2, 1, 3], center=true);
    }
}

// Thorax (pro/meso/metathorax combined)
module thorax() {
    union() {
        // Main thorax segment
        scale([1, 0.8, 0.6]) sphere(r=8);

        // Thoracic shield detail
        for(i=[-1:1]) {
            translate([i*2, 0, 1]) 
                rotate([90, 0, 0]) cylinder(h=1, r=1.5);
        }
    }
}

// Abdomen (elongated with segments)
module abdomen() {
    union() {
        // Main abdomen segments
        for(i=[0:5]) {
            translate([0, 0, -i*2.5]) 
                scale([1 - i*0.08, 0.7 - i*0.05, 0.4]) 
                sphere(r=8 - i);
        }

        // Segment lines
        for(i=[1:4]) {
            translate([0, 0, -i*2.5 + 1.2]) 
                rotate([90, 0, 0]) 
                cylinder(h=0.5, r=6 - i*0.5);
        }
    }
}

// Raptorial front leg (folded)
module raptorial_leg() {
    // Coxa
    rotate([90, 0, 0]) cylinder(h=4, r=2);

    // Femur (with spines)
    translate([0, 0, -4]) rotate([0, 30, 0]) {
        cylinder(h=8, r=1.5);
        // Spines along femur
        for(i=[1:3]) {
            translate([0, 0, -i*2]) 
                rotate([0, 0, 90]) 
                cylinder(h=1.5, r=0.3);
        }
    }

    // Tibia
    translate([0, 0, -12]) rotate([0, 60, 0]) {
        cylinder(h=6, r=1);
        // Tibial spines
        for(i=[1:2]) {
            translate([0, 0, -i*2]) 
                rotate([0, 0, 90]) 
                cylinder(h=1, r=0.2);
        }
    }

    // Tarsus
    translate([0, 0, -18]) rotate([0, 90, 0]) 
        cylinder(h=4, r=0.5);
}

// Middle leg
module middle_leg() {
    // Coxa
    rotate([90, 0, 0]) cylinder(h=3, r=1.5);

    // Femur
    translate([0, 0, -3]) rotate([0, 45, 0]) 
        cylinder(h=6, r=1);

    // Tibia
    translate([0, 0, -9]) rotate([0, 60, 0]) 
        cylinder(h=5, r=0.8);

    // Tarsus
    translate([0, 0, -14]) rotate([0, 75, 0]) 
        cylinder(h=3, r=0.4);
}

// Hind leg
module hind_leg() {
    // Coxa
    rotate([90, 0, 0]) cylinder(h=2.5, r=1.2);

    // Femur
    translate([0, 0, -2.5]) rotate([0, 30, 0]) 
        cylinder(h=5, r=0.8);

    // Tibia
    translate([0, 0, -7.5]) rotate([0, 45, 0]) 
        cylinder(h=4, r=0.6);

    // Tarsus
    translate([0, 0, -11.5]) rotate([0, 60, 0]) 
        cylinder(h=2.5, r=0.3);
}

// Antenna module
module antenna() {
    // Base
    sphere(r=0.8);

    // Segments
    for(i=[1:10]) {
        translate([0, 0, -i*1.5]) 
            rotate([0, 0, i*10]) 
            cylinder(h=1.5, r=0.5 - i*0.04);
    }
}

// Generate the mantis
mantis();

