// Marabou Stork - Realistic Style, Medium Complexity
// Designed for 3D printing with no overhangs

$fn = 36;  // Smooth curves for realistic appearance

// Overall dimensions
bird_length = 80;
bird_height = 45;
wing_span = 75;

// Main body module
module body() {
    // Torso - elongated sphere
    translate([0, 0, 25])
        scale([1.2, 0.8, 1])
        sphere(12);

    // Abdomen - smaller sphere
    translate([-15, 0, 18])
        scale([0.9, 0.7, 0.6])
        sphere(10);
}

// Head with distinctive features
module head() {
    // Main head shape
    translate([30, 0, 35])
        rotate([0, 20, 0])
        scale([1.3, 0.9, 1.1])
        sphere(8);

    // Beak - long and tapered
    translate([42, 0, 35])
        rotate([0, -10, 0])
        cylinder(18, 2.5, 1);

    // Throat pouch
    translate([35, 0, 28])
        scale([1, 0.8, 0.6])
        sphere(5);

    // Eyes
    for(side = [-1, 1]) {
        translate([32, side*4, 37])
            sphere(1.5);
    }
}

// Legs and feet
module legs() {
    // Left leg
    translate([5, -8, 0]) {
        cylinder(20, 2, 2);
        // Foot
        translate([0, 0, -2])
            rotate([90, 0, 0])
            cylinder(12, 1.5, 1.5);
        // Toes
        for(angle = [-30, 0, 30]) {
            rotate([0, 0, angle])
                translate([10, 0, 0])
                    rotate([90, 0, 0])
                    cylinder(8, 1, 0.5);
        }
    }

    // Right leg (mirrored)
    translate([5, 8, 0]) {
        cylinder(20, 2, 2);
        // Foot
        translate([0, 0, -2])
            rotate([90, 0, 0])
            cylinder(12, 1.5, 1.5);
        // Toes
        for(angle = [-30, 0, 30]) {
            rotate([0, 0, angle])
                translate([10, 0, 0])
                    rotate([90, 0, 0])
                    cylinder(8, 1, 0.5);
        }
    }
}

// Wings with feather details
module wings() {
    // Left wing
    translate([0, -20, 30]) {
        rotate([0, 0, -20])
            scale([1.2, 0.3, 0.1])
            sphere(20);

        // Primary feathers
        for(i = [0:4]) {
            translate([15 + i*3, -5 - i*2, 0])
                rotate([0, 0, -15 - i*5])
                scale([1, 0.2, 0.05])
                cube([5, 8, 1]);
        }
    }

    // Right wing (mirrored)
    mirror([0, 1, 0])
        translate([0, -20, 30]) {
            rotate([0, 0, -20])
                scale([1.2, 0.3, 0.1])
                sphere(20);

            // Primary feathers
            for(i = [0:4]) {
                translate([15 + i*3, -5 - i*2, 0])
                    rotate([0, 0, -15 - i*5])
                    scale([1, 0.2, 0.05])
                    cube([5, 8, 1]);
            }
        }
}

// Tail feathers
module tail() {
    translate([-25, 0, 25]) {
        rotate([0, 0, 180])
            scale([1.5, 0.8, 0.1])
            sphere(10);

        // Individual tail feathers
        for(i = [0:3]) {
            translate([-5 - i*2, (i-1.5)*3, 0])
                rotate([0, 0, (i-1.5)*10])
                scale([1.2, 0.15, 0.05])
                cube([8, 5, 1]);
        }
    }
}

// Neck connection
module neck() {
    translate([20, 0, 30])
        rotate([0, 20, 0])
        scale([1, 0.7, 0.7])
        cylinder(15, 5, 3);
}

// Assemble the marabou
union() {
    body();
    neck();
    head();
    wings();
    tail();
    legs();
}

