        translate([5, -4, 2]) sphere(d=3);

        // Nose
        translate([11, 0, 0]) scale([1.2, 0.8, 0.6]) sphere(d=3);
    }
}

// Ear module
module ear() {
    difference() {
        // Outer ear
        scale([1, 0.7, 1.5]) sphere(d=6);

        // Inner ear
        translate([0, 0, 1]) scale([0.7, 0.5, 1.2]) sphere(d=4);
