// Realistic Marmot - Medium Complexity
// Designed for 3D printing with no overhangs

$fn = 32;  // Smooth curves for realistic appearance

// Main body module
module marmot() {
    // Body
    difference() {
        union() {
            // Main torso
            scale([1, 0.8, 0.7])
                sphere(20);

            // Belly bulge
            translate([0, -5, -10])
                scale([0.8, 0.9, 0.5])
                    sphere(15);
        }

        // Leg holes
        for(x = [-12, 12]) {
            translate([x, 0, -15])
                rotate([90, 0, 0])
                    cylinder(h=10, r=6);
        }
    }

    // Head
    translate([0, 20, 5]) {
        difference() {
            // Main head shape
            scale([0.9, 1.2, 0.9])
                sphere(12);

            // Snout area
            translate([0, 15, -2])
                scale([0.8, 0.6, 0.6])
                    sphere(8);
        }

        // Ears
        for(x = [-8, 8]) {
            translate([x, 5, 10]) {
                rotate([45 * (x > 0 ? 1 : -1), 0, 0])
                    scale([0.3, 1, 0.5])
                        cylinder(h=8, r1=4, r2=2);
            }
        }

        // Eyes
        for(x = [-5, 5]) {
            translate([x, 12, 3])
                sphere(1.5);
        }

        // Nose
        translate([0, 18, -1])
            scale([1.2, 0.8, 0.8])
                sphere(2);
    }

    // Tail
    translate([0, -25, 0])
        rotate([45, 0, 0])
            scale([0.6, 1, 0.6])
                cylinder(h=15, r1=6, r2=4);

    // Legs
    for(x = [-12, 12]) {
        translate([x, 0, -20]) {
            // Upper leg
            rotate([90, 0, 0])
                cylinder(h=12, r=6);

            // Paw
            translate([0, 0, -12])
                scale([1.2, 1.2, 0.5])
                    sphere(5);

            // Toes
            for(angle = [0, 120, 240]) {
                rotate([0, 0, angle])
                    translate([4, 0, -14])
                        scale([0.3, 0.3, 1])
                            cylinder(h=3, r=2);
            }
        }
    }

    // Front paws
    for(x = [-10, 10]) {
        translate([x, 15, -8]) {
            // Forearm
            rotate([90, 0, 0])
                cylinder(h=8, r=5);

            // Paw
            translate([0, 0, -8])
                scale([1.2, 1.2, 0.5])
                    sphere(4);

            // Toes
            for(angle = [0, 120, 240]) {
                rotate([0, 0, angle])
                    translate([3, 0, -10])
                        scale([0.3, 0.3, 1])
                            cylinder(h=2, r=1.5);
            }
        }
    }

    // Whiskers
    for(side = [-1, 1]) {
        for(i = [0:2]) {
            rotate([0, 0, side * (30 + i * 15)])
                translate([side * 8, 16, 1])
                    cylinder(h=8, r=0.3);
        }
    }
}

// Create the marmot
marmot();

