module megapode() {
    // Overall scale
    scale(0.4)
    union() {
        // Body
        translate([0, 0, 4])
        rotate([0, -20, 0])
        scale([1.1, 0.9, 1.2])
        sphere(r = 10);

        // Neck
        translate([0, 0, 12])
        rotate([0, 20, 0])
        cylinder(h = 8, r1 = 4, r2 = 3, $fn = 16);

        // Head
        translate([0, 0, 21])
        rotate([0, 20, 0])
        scale([0.9, 0.8, 1.1])
        sphere(r = 4);

        // Beak
        translate([0, 0, 24])
        rotate([0, 20, 0])
        cylinder(h = 4, r1 = 1.5, r2 = 0.5, $fn = 8);

        // Tail
        translate([-10, 0, 4])
        rotate([0, -30, 0])
        scale([1.2, 1, 0.6])
        sphere(r = 5);

        // Legs
        for (side = [-1, 1]) {
            // Upper leg
            translate([side * 4, 0, 2])
            rotate([0, 0, side * 15])
            cylinder(h = 6, r = 1.5, $fn = 8);

            // Lower leg
            translate([side * 5, 0, -2])
            rotate([0, 0, side * 10])
            cylinder(h = 5, r = 1.2, $fn = 8);

            // Foot
            translate([side * 5.5, 0, -5.5])
            rotate([0, 0, side * 10])
            scale([1.5, 1, 0.5])
            sphere(r = 2);

            // Toes
            for (t = [0, 1, -1]) {
                translate([side * (5.5 + t * 1.5), 0, -6.5])
                rotate([0, 0, side * (10 + t * 20)])
                cylinder(h = 2, r = 0.6, $fn = 6);
            }
        }

        // Wings
        for (side = [-1, 1]) {
            translate([side * 8, 0, 8])
            rotate([0, side * 20, 0])
            scale([1.2, 0.3, 0.8])
            sphere(r = 6);

            // Wing feathers
            for (i = [0:2]) {
                translate([side * (9 + i), 0, 8 - i * 2])
                rotate([0, side * (20 + i * 10), 0])
                scale([0.5, 0.2, 0.3])
                sphere(r = 3);
            }
        }

        // Eye
        for (side = [-1, 1]) {
            translate([side * 1.5, 1.8, 21.5])
            scale([0.7, 0.7, 0.5])
            sphere(r = 1);

            // Pupil
            translate([side * 1.5, 2.2, 21.8])
            scale([0.4, 0.4, 0.3])
            sphere(r = 0.5);
        }
    }
}

// Create the model
megapode();

