// Realistic Minnow - Medium Complexity
// Total length ~80mm

// Main body parameters
body_length = 70;
body_height = 15;
body_width = 12;

// Head parameters
head_length = 20;
head_height = 18;
head_width = 14;

// Tail parameters
tail_length = 25;
tail_height = 20;
tail_width = 2;

// Fin parameters
dorsal_height = 12;
dorsal_length = 25;
pectoral_length = 15;
pectoral_height = 8;

// Eye parameters
eye_radius = 3;
eye_offset = 8;

// Scale parameters
scale_size = 2;
scale_rows = 8;
scale_cols = 12;

// Wall thickness for 3D printing
wall_thickness = 1.2;

module minnow_body() {
    // Main tapered body
    hull() {
        // Front of body (wider)
        translate([0, 0, 0])
            scale([1, 1, 0.8])
                sphere(d=head_width, $fn=20);

        // Mid body
        translate([-body_length/3, 0, 0])
            scale([1.2, 1, 0.9])
                sphere(d=body_width*0.9, $fn=20);

        // Tail junction
        translate([-body_length*0.7, 0, 0])
            scale([0.8, 0.7, 0.6])
                sphere(d=body_width*0.7, $fn=20);
    }
}

module fish_head() {
    // Main head shape
    translate([head_length/2, 0, 0])
        scale([1.2, 1, 0.9])
            sphere(d=head_width, $fn=25);

    // Snout extension
    translate([head_length, 0, -2])
        scale([1.5, 0.8, 0.6])
            sphere(d=head_width*0.7, $fn=20);
}

module caudal_fin() {
    // Tail fin (bifurcated)
    union() {
        // Upper lobe
        translate([-body_length*0.8, 0, tail_height/2])
            rotate([0, 0, 20])
                scale([1, 0.3, 1])
                    cylinder(h=tail_length, d1=tail_height*1.2, d2=tail_height*0.3, $fn=3);

        // Lower lobe
        translate([-body_length*0.8, 0, -tail_height/2])
            rotate([0, 0, -20])
                scale([1, 0.3, 1])
                    cylinder(h=tail_length, d1=tail_height*1.2, d2=tail_height*0.3, $fn=3);
    }
}

module dorsal_fin() {
    // Main dorsal fin
    translate([-body_length/3, 0, body_height/2 + dorsal_height/3])
        rotate([90, 0, 0])
            scale([1, 0.3, 1])
                cylinder(h=dorsal_length, d1=dorsal_height, d2=dorsal_height/2, $fn=3);

    // Fin rays
    for(i = [-1:0.5:1]) {
        translate([-body_length/3 + i*8, 0, body_height/2 + dorsal_height/2])
            rotate([90, 0, 0])
                scale([0.1, 0.1, 1])
                    cylinder(h=dorsal_length*0.9, d=dorsal_height/8, $fn=4);
    }
}

module pectoral_fin(side) {
    // Side fins
    translate([head_length/2, side * (head_width/2 + 2), -body_height/4])
        rotate([0, side * 20, side * 30])
            scale([1, 0.2, 1])
                cylinder(h=pectoral_length, d1=pectoral_height, d2=pectoral_height/2, $fn=3);
}

module pelvic_fin(side) {
    // Pelvic fins
    translate([-body_length/4, side * (body_width/2 + 1), -body_height/2])
        rotate([0, 0, side * -30])
            scale([0.8, 0.2, 1])
                cylinder(h=pectoral_length*0.7, d1=pectoral_height*0.8, d2=pectoral_height*0.4, $fn=3);
}

module anal_fin() {
    // Anal fin
    translate([-body_length*0.6, 0, -body_height/2 - dorsal_height/3])
        rotate([90, 0, 0])
            scale([1, 0.3, 1])
                cylinder(h=dorsal_length*0.7, d1=dorsal_height*0.8, d2=dorsal_height/3, $fn=3);
}

module fish_eyes() {
    // Eyes
    for(side = [-1, 1]) {
        translate([head_length/2 - 2, side * eye_offset, head_height/4])
            sphere(r=eye_radius, $fn=20);

        // Pupil
        translate([head_length/2, side * eye_offset, head_height/4])
            sphere(r=eye_radius*0.5, $fn=15);
    }
}

module body_scales() {
    // Simplified scale pattern using small spheres
    for(row = [1:scale_rows]) {
        for(col = [1:scale_cols]) {
            z_pos = -body_height/2 + row * (body_height/(scale_rows+1));
            x_pos = -col * (body_length/(scale_cols+1));

            if(x_pos < -head_length && x_pos > -body_length*0.7) {
                translate([x_pos, 0, z_pos])
                    sphere(r=scale_size*0.3, $fn=8);
            }
        }
    }
}

module mouth() {
    // Mouth opening
    translate([head_length, 0, -head_height/4])
        rotate([0, 90, 0])
            cylinder(h=head_length/3, d=head_height/3, $fn=20);
}

// Assemble the minnow
union() {
    // Main body parts
    minnow_body();
    fish_head();

    // Fins
    caudal_fin();
    dorsal_fin();
    anal_fin();

    // Side fins
    pectoral_fin(1);
    pectoral_fin(-1);
    pelvic_fin(1);
    pelvic_fin(-1);

    // Details
    fish_eyes();

    // Hollow out for 3D printing
    difference() {
        children();

        // Internal cavity
        scale([0.8, 0.7, 0.7])
            translate([0, 0, 0])
                cube([body_length*1.2, body_width, body_height*1.2], center=true);
    }
}

// Add mouth (cutout)
difference() {
    children();
    mouth();
}

