    translate([10, 3, 8]) 
    rotate([0, 90, 0]) 
    sphere(r=1.5, $fn=16);
}

// Tail feathers
module tail() {
    translate([-12, 0, 4]) 
    rotate([0, -15, 0]) 
    for(i = [-1,0,1]) {
        rotate([0, i*20, 0]) 
        scale([1.5, 0.1, 0.3]) 
        cylinder(h=15, r1=3, r2=0.5, $fn=24);
    }
}

// Wings
module wings() {
    // Right wing
    translate([0, 8, 4]) 
    rotate([90, 0, 0]) 
    scale([1.5, 0.8, 0.5]) 
    difference() {
        sphere(r=8, $fn=32);
        translate([0, 0, -8]) 
        cube(16, center=true);
    }

    // Left wing
    translate([0, -8, 4]) 
    rotate([-90, 0, 0]) 
    scale([1.5, 0.8, 0.5]) 
    difference() {
        sphere(r=8, $fn=32);
        translate([0, 0, -8]) 
        cube(16, center=true);
