// Mockingbird - Realistic style, medium complexity
// Designed for 3D printing (~75mm wingspan)

// Parameters for easy scaling
body_length = 35;
body_height = 20;
wingspan = 75;
tail_length = 25;
head_size = 15;
beak_length = 8;

// Main module
module mockingbird() {
    union() {
        // Body
        body();

        // Head
        translate([body_length/2 - 5, 0, body_height/2 - 2])
            head();

        // Wings
        translate([-body_length/4, 0, body_height/2])
            wing(true);
        translate([-body_length/4, 0, body_height/2])
            mirror([0,1,0])
            wing(false);

        // Tail
        translate([-body_length/2 - tail_length/2, 0, body_height/4])
            tail();

        // Legs
        translate([body_length/4, body_height/2 - 3, -body_height/2])
            rotate([0,90,0])
            leg();
        translate([body_length/4, -body_height/2 + 3, -body_height/2])
            rotate([0,90,0])
            leg();
    }
}

// Body module - oval shaped
module body() {
    difference() {
        // Main body
        scale([1.5, 1, 1])
        sphere(d=body_height);

        // Flatten bottom
        translate([-body_length, -body_height, -body_height/2 - 1])
            cube([body_length*2, body_height*2, body_height]);
    }
}

// Head module with beak and eyes
module head() {
    union() {
        // Head sphere
        sphere(d=head_size);

        // Beak
        translate([head_size/2, 0, 0])
        rotate([0,-30,0])
        scale([1, 0.7, 0.7])
        cylinder(h=beak_length, d1=head_size/3, d2=0);

        // Eyes
        translate([head_size/4, head_size/3, head_size/4])
        sphere(d=head_size/6);
        translate([head_size/4, -head_size/3, head_size/4])
        sphere(d=head_size/6);
    }
}

// Wing module with feather details
module wing(left=true) {
    difference() {
        // Main wing shape
        union() {
            // Upper wing
            translate([0, 0, 0])
                scale([1.5, 0.3, 1])
                sphere(d=wingspan/2);

            // Lower wing section
            translate([-wingspan/6, 0, -wingspan/8])
                scale([1.2, 0.25, 0.8])
                sphere(d=wingspan/3);
        }

        // Cut wing shape
        for(i=[0:3]) {
            rotate([0, 0, i*15 - 30])
            translate([i*5, 0, -wingspan/4])
                cube([15, wingspan/2, wingspan/2], center=true);
        }
    }
}

// Tail module with feather separation
module tail() {
    difference() {
        // Main tail
        scale([1.5, 0.3, 1])
        sphere(d=tail_length);

        // Feather separation cuts
        for(i=[-1:1]) {
            translate([0, i*tail_length/4, 0])
            rotate([0, i*10, 0])
            cube([tail_length*2, 1, tail_length*2], center=true);
        }
    }
}

// Leg module with toes
module leg() {
    union() {
        // Main leg
        cylinder(h=8, d=3);

        // Foot
        translate([0, 0, -4])
            sphere(d=4);

        // Toes (3 forward, 1 back)
        for(i=[-1:1]) {
            translate([i*2, 0, -6])
            rotate([i*20, 0, 0])
            cylinder(h=4, d=1);
        }
        translate([0, -2, -6])
        rotate([-20, 0, 0])
        cylinder(h=3, d=1);
    }
}

// Render the bird
mockingbird();

