        translate([0, 0, 15]) 
            rotate([90, 0, 0]) 
                cylinder(h=60, d1=20, d2=15);

        // Rump
        translate([-30, 0, 15]) 
            sphere(d=18);

        // Chest
        translate([30, 0, 15]) 
