}

// Head
module head() {
    union() {
        // Main head sphere
        translate([40, 0, 22]) 
            sphere(d=14);

        // Snout
        translate([48, 0, 22]) 
            rotate([0, -10, 0]) 
                cylinder(h=12, d1=8, d2=5);

        // Ears
        for(side = [-1, 1]) {
            translate([38, side*6, 30]) 
                rotate([side*20, 0, 0]) 
                    cylinder(h=8, d1=3, d2=6);
        }
    }
}

// Legs
module legs() {
    for(side = [-1, 1]) {
        // Front legs
        translate([28, side*6, 0]) {
            cylinder(h=12, d=4);
            translate([0, 0, 12]) 
                sphere(d=4.5);
        }

        // Back legs
        translate([-20, side*6, 0]) {
            cylinder(h=15, d=5);
            translate([0, 0, 15]) 
                sphere(d=5.5);
        }
    }
}

// Tail
module tail() {
    hull() {
        translate([-45, 0, 15]) 
            sphere(d=8);
        translate([-65, 0, 25]) 
            sphere(d=5);
        translate([-80, 0, 20]) 
            sphere(d=3);
    }
}

// Eyes
module eyes() {
    for(side = [-1, 1]) {
        translate([46, side*3.5, 24]) {
            difference() {
                sphere(d=3);
