$fn = 24; // Resolution for curved surfaces

module mongoose() {
    // Main body
    union() {
        // Torso - elongated oval
        translate([0, 0, 10]) 
            scale([1.2, 0.8, 0.6])
            sphere(r=15);

        // Belly
        translate([0, 0, 5])
            scale([1.1, 0.7, 0.3])
            sphere(r=15);
    }

    // Head
    translate([-20, 0, 20]) 
        rotate([0, -10, 0])
        union() {
            // Main head shape
            scale([1.3, 1, 1])
            sphere(r=10);

            // Snout
            translate([-8, 0, -2])
                scale([1.5, 0.7, 0.7])
                sphere(r=6);
        }

    // Ears
    for(side = [-1, 1]) {
        translate([-25, side*8, 25])
            rotate([0, side*20, 0])
            scale([0.3, 0.5, 1])
            cylinder(h=8, r1=5, r2=2);
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([-25, side*5, 20])
            color("black")
            sphere(r=2);
    }

    // Nose
    translate([-32, 0, 18])
        color("black")
        rotate([90, 0, 0])
        cylinder(h=3, r1=2, r2=0);

    // Tail
    hull() {
        translate([18, 0, 15])
            sphere(r=5);
        translate([35, 0, 20])
            scale([0.5, 0.5, 1])
            sphere(r=5);
    }

    // Legs
    for(x = [-8, 8]) {
        for(side = [-1, 1]) {
            translate([x, side*10, 0])
                rotate([10, side*5, 0])
                union() {
                    // Upper leg
                    cylinder(h=12, r=4);

                    // Paw
                    translate([0, 0, 12])
                        sphere(r=4);
                }
        }
    }

    // Front legs
    for(side = [-1, 1]) {
        translate([-15, side*10, 5])
            rotate([10, side*10, 0])
            union() {
                cylinder(h=10, r=3.5);
                translate([0, 0, 10])
                    sphere(r=3.5);
            }
    }

    // Stripes on back
    for(i = [-10:5:15]) {
        translate([i, 0, 20])
            scale([1, 0.8, 0.1])
            rotate([0, 90, 0])
            cylinder(h=0.5, r=8);
    }
}

// Main assembly
mongoose();

