// Moorhen (Gallinula chloropus) – medium complexity, printable
// Overall length ~90 mm, height ~45 mm
// Thick walls (≥2 mm), no unsupported overhangs

$fa = 2; $fs = 1;

module body_shell() {
    // Main torso – two fused ellipsoids
    translate([0,0,18])
        scale([1, 0.6, 0.7])
        sphere(r = 20);            // chest & back

    translate([-22,0,8])
        scale([0.9, 0.5, 0.5])
        sphere(r = 18);            // abdomen
}

module tail() {
    // Slightly raised tail feathers
    translate([-42,0,12])
        rotate([0,25,0])
        scale([1.2, 0.3, 0.4])
        sphere(r = 12);
}

module neck() {
    // S-curved neck
    translate([18,0,18])
        rotate([0,-30,0])
        scale([0.7, 0.5, 0.5])
        cylinder(h = 22, r1 = 7, r2 = 5);
}

module head() {
    // Head with red frontal shield
    translate([34,0,25])
        scale([0.9, 0.6, 0.7])
        sphere(r = 9);

    // Red shield
    color("red")
    translate([40,0,30])
        scale([0.4, 0.25, 0.4])
        sphere(r = 8);
}

module beak() {
    // Yellow-tipped beak (cone)
    color("yellow")
    translate([43,0,25])
        rotate([0,-15,0])
        cylinder(h = 12, r1 = 3, r2 = 0);
}

module eye() {
    // Simple black sphere
    color("black")
    translate([37, 5, 30])
        sphere(r = 2);
}

module wing() {
    // Folded wing on side, no unsupported angles
    translate([0,-8,22])
        rotate([0,0,-20])
        scale([1.4, 0.25, 0.6])
        sphere(r = 15);
}

module leg(side) {        // side = 1 or -1
    // Thigh
    translate([8, side*10, 8])
        rotate([0,15,0])
        cylinder(h = 10, r = 2.5);

    // Shank
    translate([15, side*12, 6.5])
        rotate([0,15,0])
        cylinder(h = 14, r = 2);

    // Foot – three forward toes joined by web
    // central toe
    translate([27, side*12, 2])
        rotate([90,0,0])
        cylinder(h = 12, r = 1.5);

    color("darkslategray")
    // web
    translate([30, side*12, 1])
        rotate([90,0,0])
        scale([1, 0.3, 0.2])
        cylinder(h = 8, r = 4);

    // outer toes (mirrored)
    for(a = [-30, 30]) {
        rotate([0,0, a])
        translate([27, side*12, 2])
            rotate([90,0,0])
            cylinder(h = 10, r = 1.2);
    }
}

// Main assembly
union() {
    body_shell();
    tail();
    neck();
    head();
    beak();
    mirror([0,1,0]) eye();  // two eyes
    wing();
    mirror([0,1,0]) wing();

    for(s = [-1,1]) leg(s);
}

