module moose() {
    // Main body - elongated sphere
    scale([1.8, 0.8, 0.6])
    sphere(r=10, $fn=32);

    // Neck - tapered cylinder
    translate([8, 0, 2]) 
    rotate([0, -15, 0])
    cylinder(h=15, r1=4.5, r2=3, $fn=24);

    // Head - sphere with snout extension
    translate([21, 0, 0]) {
        // Base head shape
        scale([1.1, 0.8, 0.9])
        sphere(r=7, $fn=32);

        // Muzzle
        translate([5, 0, -2])
        scale([1.5, 0.7, 0.6])
        sphere(r=4, $fn=24);

        // Jawline
        translate([2, 0, -5])
        rotate([20, 0, 0])
        scale([1.5, 0.8, 0.4])
        sphere(r=3, $fn=24);
    }

    // Legs - positioned symmetrically
    module leg() {
        translate([0, 0, -5])
        cylinder(h=15, r1=2, r2=1.5, $fn=16);
        translate([0, 0, -19])
        scale([1, 1.3, 0.8])
        sphere(r=2.5, $fn=16);
    }

    // Front legs
    translate([-5, -5, -10]) leg();
    translate([-5, 5, -10]) leg();

    // Hind legs
    translate([-12, -6, -8]) rotate([0, 10, 0]) leg();
    translate([-12, 6, -8]) rotate([0, 10, 0]) leg();

    // Tail
    translate([-15, 0, 3])
    rotate([70, 0, 0])
    cylinder(h=8, r1=1.2, r2=0.1, $fn=12);

    // Antlers - main beams
    module antler_beam() {
        rotate([-60, 0, 0])
        cylinder(h=20, r1=1.2, r2=0.5, $fn=12);
    }

    // Tines (branches)
    module tine(rot_y, length) {
        rotate([0, rot_y, 0])
        cylinder(h=length, r1=0.7, r2=0.1, $fn=10);
    }

    // Full antler assembly
    module antler() {
        antler_beam();

        // First tine
        translate([0, 0, 15])
        rotate([20, 0, 0])
        tine(10, 10);

        // Second tine
        translate([0, 0, 10])
        rotate([40, 0, 0])
        tine(-15, 8);

        // Third tine
        translate([0, 0, 5])
        rotate([60, 0, 0])
        tine(5, 6);
    }

    // Position antlers on head
    translate([20, 0, 8]) {
        // Left antler
        translate([0, 4, 0])
        rotate([0, 5, -15])
        antler();

        // Right antler
        translate([0, -4, 0])
        rotate([0, 5, 15])
        mirror([0,1,0])
        antler();
    }

    // Ears
    module ear() {
        rotate([0, 90, 0])
        scale([1, 1.5, 1])
        cylinder(h=1, r1=0, r2=3, $fn=16);
    }

    translate([24, 5, 3]) rotate([0, 10, -30]) ear();
    translate([24, -5, 3]) rotate([0, 10, 30]) ear();

    // Eyes
    translate([25, 3, 2]) sphere(r=1, $fn=12);
    translate([25, -3, 2]) sphere(r=1, $fn=12);
}

// Position moose centered at origin
moose();

