// Realistic medium-complexity 3D printable moose
// Approx 80 mm long, 60 mm tall, 25 mm wide
// All parts unioned so it is manifold and printable

// Body
module body() {
    // Main torso
    translate([0,0,30]) scale([1.5,0.7,1]) sphere(d=40,$fn=48);

    // Belly bulge
    translate([0,0,18]) scale([1.4,0.6,0.6]) sphere(d=35,$fn=32);
}

// Head
module head() {
    // Skull
    translate([-35,0,48]) rotate([0,-10,0]) scale([1.2,0.8,0.9]) sphere(d=25,$fn=32);

    // Muzzle
    translate([-48,0,42]) rotate([0,-15,0]) scale([1.5,0.7,0.7]) sphere(d=15,$fn=24);

    // Nostrils
    for(x=[-50,-46]) {
        translate([x,-4,40]) rotate([0,-15,0]) scale([1,0.5,0.5]) cylinder(h=3,d1=4,d2=0,$fn=8);
        translate([x,4,40]) rotate([0,-15,0]) scale([1,0.5,0.5]) cylinder(h=3,d1=4,d2=0,$fn=8);
    }

    // Eyes
    translate([-38,-8,50]) sphere(d=3,$fn=12);
    translate([-38,8,50]) sphere(d=3,$fn=12);
}

// Ears
module ear(side) {
    translate([-30,side*12,55]) rotate([0,0,side*20]) 
        scale([0.3,1,0.8]) rotate([30,0,0]) cylinder(h=12,d1=8,d2=4,$fn=16);
}

// Antler branch
module antler_branch(len,angle) {
    rotate([0,0,angle]) rotate([25,0,0]) cylinder(h=len,d1=3,d2=2,$fn=8);
    if(len>6) {
        translate([0,0,len*0.7]) antler_branch(len*0.7,25);
        translate([0,0,len*0.7]) antler_branch(len*0.7,-25);
    }
}

// Antler
module antler(side) {
    translate([-35,side*10,60]) rotate([0,0,side*15]) {
        // Main beam
        cylinder(h=25,d1=4,d2=3,$fn=12);
        translate([0,0,20]) rotate([30,0,0]) antler_branch(15,0);
        translate([0,0,15]) rotate([20,0,0]) antler_branch(12,30);
        translate([0,0,10]) rotate([15,0,0]) antler_branch(10,-30);
    }
}

// Leg
module leg(pos,angle) {
    translate(pos) rotate([angle,0,0]) {
        // Upper leg
        cylinder(h=25,d1=7,d2=5,$fn=16);
        translate([0,0,22]) rotate([15,0,0]) {
            // Lower leg
            cylinder(h=20,d1=5,d2=4,$fn=16);
            translate([0,0,18]) rotate([10,0,0]) {
                // Hoof
                scale([0.8,0.6,0.4]) cylinder(h=8,d1=6,d2=7,$fn=16);
            }
        }
    }
}

// Tail
module tail() {
    translate([20,0,35]) rotate([0,10,0]) scale([0.5,0.5,1.2]) sphere(d=8,$fn=16);
}

// Assemble moose
union() {
    body();
    head();
    ear(-1);
    ear(1);
    antler(-1);
    antler(1);
    tail();

    // Front legs
    leg([-20,-12,25],-20);
    leg([-20,12,25],-20);

    // Rear legs
    leg([20,-10,25],20);
    leg([20,10,25],20);
}

