// Realistic Mite Model
// Medium complexity OpenSCAD design for 3D printing

$fn = 32;

// Main body parameters
body_length = 40;
body_width = 25;
body_height = 15;

// Leg parameters
leg_length = 35;
leg_diameter = 2;
leg_segments = 8;
num_legs = 8;

// Head parameters
head_radius = 12;
eye_radius = 2.5;
antenna_length = 8;
antenna_diameter = 1;

// Mouthparts
chelicera_length = 6;
chelicera_diameter = 1.5;

module mite_body() {
    // Main body - egg shaped
    scale([1, 0.8, 0.6])
    sphere(d=body_length);

    // Body segmentation
    for(i = [1:3]) {
        translate([0, 0, -body_height/3 + i*body_height/6])
        scale([1 - i*0.1, 1 - i*0.1, 0.1])
        sphere(d=body_length - 5);
    }
}

module leg_segment(length, diameter, angle) {
    rotate([0, angle, 0])
    translate([length/2, 0, 0])
    rotate([90, 0, 0])
    cylinder(h=length, d=diameter, center=true);
}

module leg(leg_num, side) {
    angle = leg_num * 45 + 20;
    base_angle = side * 60 - 30;

    translate([side * body_width/3, 0, -body_height/4 + leg_num * body_height/10])
    rotate([0, 0, base_angle]) {
        // First segment
        leg_segment(leg_length/3, leg_diameter, 0);

        // Second segment
        translate([leg_length/3, 0, 0])
        rotate([0, 45, 0])
        leg_segment(leg_length/3, leg_diameter*0.9, 0);

        // Third segment
        translate([leg_length/2, 0, 0])
        rotate([0, -45, 0])
        leg_segment(leg_length/3, leg_diameter*0.8, 0);

        // Claw
        translate([leg_length*0.85, 0, 0])
        rotate([0, -90, 0])
        scale([0.5, 0.5, 0.5])
        sphere(d=leg_diameter*2);
    }
}

module head() {
    translate([-body_length/2 - head_radius, 0, 0])
    rotate([0, 0, 90])
    sphere(r=head_radius);

    // Eyes
    for(i = [-1:1]) {
        translate([-body_length/2 - head_radius*0.7, i*head_radius*0.4, head_radius*0.7])
        sphere(r=eye_radius);
    }

    // Antennae
    translate([-body_length/2 - head_radius, head_radius*0.6, head_radius*0.4])
    rotate([0, -30, 0])
    cylinder(h=antenna_length, d=antenna_diameter);

    translate([-body_length/2 - head_radius, -head_radius*0.6, head_radius*0.4])
    rotate([0, -30, 0])
    cylinder(h=antenna_length, d=antenna_diameter);

    // Chelicerae (mouthparts)
    for(i = [-1:1]) {
        translate([-body_length/2 - head_radius*1.2, i*head_radius*0.2, -head_radius*0.2])
        rotate([0, -45, i*20])
        cylinder(h=chelicera_length, d=chelicera_diameter);
    }
}

module body_texture() {
    // Small bumps for texture
    for(i = [1:20]) {
        rotate([0, 0, i*18])
        translate([random(5, body_length/2 - 5), random(-body_width/2 + 5, body_width/2 - 5), random(-body_height/2 + 2, body_height/2 - 2)])
        sphere(d=1);
    }
}

// Main assembly
union() {
    // Body
    mite_body();

    // Head
    head();

    // Legs
    for(i = [0:num_legs/2 - 1]) {
        leg(i, 1);
        leg(i, -1);
    }

    // Body texture
    body_texture();
}

// Add small pedestal for printing
translate([0, 0, -body_height])
cylinder(h=2, r=body_length/2 + 10, center=false);

