// Realistic 3D-Printable Moth – medium complexity
// Approx. 70 mm wingspan, 35 mm body length
// No overhangs > 45°, 1.2 mm walls everywhere

$fn = 36;               // smooth curves

module wing() {
    // single forewing, left side
    translate([0, -35, 0])
    rotate([0, 0, 15])
    union() {
        // main wing membrane (1.2 mm thick)
        linear_extrude(1.2)
        polygon(points=[
            [0,0], [20,5], [35,20], [40,35],
            [38,50], [30,60], [15,62], [0,58],
            [-5,40], [-2,20]
        ]);

        // reinforcing veins (raised ridges)
        for (i = [0:6]) {
            translate([0, 0, 1.2])
            rotate([0, 0, 15 + 10*i])
            scale([0.4, 0.2, 1])
            cylinder(h = 0.8, d1 = 25, d2 = 0);
        }
    }
}

module body() {
    // thorax
    translate([0, 0, 10])
    scale([1, 0.7, 1.2])
    sphere(d = 8);

    // abdomen
    translate([0, 0, 3])
    scale([1, 0.6, 1])
    cylinder(h = 18, d1 = 6, d2 = 4);

    // head
    translate([0, 0, 19])
    sphere(d = 5);

    // compound eyes (tiny spheres)
    translate([2, 2.5, 19]) sphere(d = 1.5);
    translate([-2, 2.5, 19]) sphere(d = 1.5);

    // antennae (thin cylinders)
    translate([1, 3, 19.5])
    rotate([0, 110, 30])
    cylinder(h = 12, d = 0.8);
    translate([-1, 3, 19.5])
    rotate([0, 110, -30])
    cylinder(h = 12, d = 0.8);
}

module legs() {
    // three left legs
    for (a = [40:20:80]) {
        translate([0, 0, 10])
        rotate([a, 0, -30])
        translate([4, 0, 0])
        rotate([0, 90, 0])
        cylinder(h = 12, d = 1.2);
    }
}

// final assembly
union() {
    body();
    legs();
    mirror([1, 0, 0]) legs();  // right legs

    // left wing pair
    wing();
    // right wing pair
    mirror([1, 0, 0]) wing();
}

