// Mudpuppy - Realistic Style, Medium Complexity
// 3D printable model using basic primitives

$fn = 32;

// Overall body length ~80mm
body_length = 60;
body_height = 15;
body_width = 20;

// Head dimensions
head_length = 20;
head_height = 12;
head_width = 15;

// Tail length
tail_length = 30;

// Leg dimensions
leg_length = 15;
leg_height = 8;
leg_width = 6;

// Main body module
module body() {
    // Central body segment
    translate([0, 0, body_height/2])
    scale([1, 0.8, 0.6])
    sphere(d=body_width);

    // Body segments decreasing in size
    for(i = [1:4]) {
        translate([-i*10, 0, body_height/2 - i*1.5])
        scale([1 - i*0.1, 0.8 - i*0.05, 0.6 - i*0.08])
        sphere(d=body_width - i*2);
    }
}

// Head module with external gills
module head() {
    // Main head shape
    translate([body_length/2 + head_length/2 - 5, 0, head_height/2])
    scale([1.2, 1, 0.9])
    sphere(d=head_width);

    // Snout
    translate([body_length/2 + head_length - 2, 0, head_height/2 - 2])
    scale([1.5, 0.7, 0.5])
    sphere(d=8);

    // Eyes
    for(side = [-1, 1]) {
        translate([body_length/2 + head_length/2 - 2, side*5, head_height/2 + 2])
        sphere(d=3);

        // Pupil
        translate([body_length/2 + head_length/2 - 1, side*5, head_height/2 + 3])
        color("black")
        sphere(d=1.5);
    }

    // External gills - feathery structures
    for(side = [-1, 1]) {
        for(i = [0:2]) {
            rotate([0, 0, side * 30])
            translate([body_length/2 + head_length/2 - 5 - i*2, side*(head_width/2 + 2 + i), head_height/2 - 2])
            cylinder(h=8, d1=1, d2=0.5);
        }
    }
}

// Tail module
module tail() {
    // Tail base
    translate([-body_length/2 - tail_length/2, 0, body_height/2 - 5])
    scale([1.5, 1, 0.3])
    sphere(d=body_width);

    // Tail fin top
    hull() {
        translate([-body_length/2 - 5, 0, body_height/2 - 2])
        sphere(d=2);
        translate([-body_length/2 - tail_length, 0, body_height/2 + 5])
        sphere(d=1);
    }

    // Tail fin bottom
    hull() {
        translate([-body_length/2 - 5, 0, body_height/2 - 8])
        sphere(d=2);
        translate([-body_length/2 - tail_length, 0, body_height/2 - 12])
        sphere(d=1);
    }
}

// Leg module for all four legs
module leg(pos_x, pos_y, side) {
    translate([pos_x, pos_y, 0])
    rotate([0, 0, side * 20])
    union() {
        // Upper leg
        translate([0, 0, leg_height/2])
        scale([1.2, 0.8, 1])
        sphere(d=leg_width);

        // Lower leg
        translate([leg_length/2 - 2, 0, -leg_height/4])
        scale([0.8, 0.6, 0.8])
        sphere(d=leg_width - 2);

        // Foot with toes
        translate([leg_length - 1, 0, -leg_height/2 - 1])
        union() {
            // Main foot
            scale([1.5, 1, 0.5])
            sphere(d=leg_width - 1);

            // Toes
            for(i = [-1, 0, 1]) {
                translate([leg_length + 1, i*2, -leg_height/2 - 1])
                scale([0.5, 0.3, 0.2])
                sphere(d=3);
            }
        }
    }
}

// Main assembly
union() {
    body();
    head();
    tail();

    // Front legs
    leg(body_length/2 - 10, body_width/2 - 5, 1);
    leg(body_length/2 - 10, -body_width/2 + 5, -1);

    // Back legs
    leg(-body_length/2 + 5, body_width/2 - 3, 1);
    leg(-body_length/2 + 5, -body_width/2 + 3, -1);
}

