// Main body
module fish_body() {
    hull() {
        // Head section
        translate([-20, 0, 0])
        scale([1.2, 0.8, 0.6])
        sphere(r=10, $fn=32);

        // Mid section
        translate([0, 0, 0])
        scale([1.8, 0.9, 0.5])
        sphere(r=8, $fn=32);

        // Tail connection
        translate([20, 0, 0])
        scale([1.0, 0.7, 0.4])
        sphere(r=6, $fn=32);
    }
}

// Tail fin
module tail_fin() {
    translate([30, 0, 0]) 
    rotate([90, 0, 0]) 
    linear_extrude(height=0.8, center=true)
    polygon(points=[
        [0, 0],
        [-10, 8],
        [-20, 0],
        [-10, -8]
    ]);
}

// Dorsal fin
module dorsal_fin() {
    translate([-5, 0, 5]) 
    rotate([90, 0, 0]) 
    linear_extrude(height=0.8, center=true)
    polygon(points=[
        [0, 0],
        [5, 8],
        [10, 0],
        [15, 12],
        [20, 0],
        [15, 8],
        [10, 0],
        [5, 5]
    ]);
}

// Anal fin (bottom fin)
module anal_fin() {
    translate([0, 0, -4]) 
    rotate([90, 0, 0]) 
    linear_extrude(height=0.8, center=true)
    polygon(points=[
        [0, 0],
        [10, -5],
        [20, 0],
        [15, -7],
        [5, -3]
    ]);
}

// Pectoral fins
module pectoral_fin() {
    rotate([0, 20, 0])
    linear_extrude(height=0.8, center=true)
    polygon(points=[
        [0, 0],
        [8, 5],
        [0, 10],
        [-5, 7]
    ]);
}

