module eye() {
    translate([-25, 5, 2])
    rotate([0, 90, 0])
    cylinder(h=3, r1=2, r2=1.5, center=true, $fn=16);
}

// Mackerel stripes pattern
module stripes() {
    for(i = [0:5]) {
        translate([-20 + i*8, 0, 0])
        rotate([0, 90, 90])
        linear_extrude(height=20, center=true, twist=15)
        square([0.8, 12], center=true);
