    difference() {
        // Main fish body
        fish_body();

        // Cut stripes into body
        stripes();
    }

    // Add fins
    tail_fin();
    dorsal_fin();
    anal_fin();

    // Add pectoral fins on both sides
    translate([-15, 7, 0]) pectoral_fin();
    translate([-15, -7, 0]) mirror([0,1,0]) pectoral_fin();

    // Add eyes on both sides
