// Overall scale factor
scaleFactor = 0.3;

// Body parameters
bodyLength = 80 * scaleFactor;
bodyHeight = 20 * scaleFactor;
bodyWidth = 15 * scaleFactor;

// Head parameters
headLength = 25 * scaleFactor;
headWidth = 18 * scaleFactor;

// Tail parameters
tailWidth = 25 * scaleFactor;
tailHeight = 12 * scaleFactor;

// Fin parameters
finLength = 15 * scaleFactor;
finWidth = 8 * scaleFactor;

// Blowhole parameters
blowholeRadius = 1.5 * scaleFactor;

// Eye parameters
eyeRadius = 1.2 * scaleFactor;

module whaleBody() {
    // Main body - elongated ellipsoid
    hull() {
        translate([-bodyLength/2, 0, 0])
            scale([1, 0.8, 0.6])
            sphere(r=bodyWidth/2, $fn=32);

        translate([bodyLength/2 - 5, 0, 2])
            scale([0.8, 0.7, 0.5])
            sphere(r=bodyWidth/2, $fn=32);
    }
}

module whaleHead() {
    // Streamlined head with pointed snout
    hull() {
        translate([bodyLength/2 - headLength/2, 0, 2])
            sphere(r=headWidth/2, $fn=32);

        translate([bodyLength/2 + headLength/2, 0, 0])
            scale([0.4, 0.3, 0.2])
            sphere(r=headWidth/2, $fn=32);
    }

    // Lower jaw
    translate([bodyLength/2 + headLength/2 - 5, 0, -bodyHeight/2])
        rotate([15, 0, 0])
        scale([1.2, 0.8, 0.4])
        sphere(r=headWidth/2.5, $fn=32);
}

module whaleTail() {
    // Tail stock
    translate([-bodyLength/2 - 5, 0, 0])
        scale([0.6, 0.4, 0.3])
        sphere(r=bodyWidth/2, $fn=32);

    // Tail flukes - two symmetrical parts
    for(side = [-1, 1]) {
        // Main fluke
        translate([-bodyLength/2 - 10, side * tailWidth/3, -2])
            rotate([0, 0, side * 20])
            scale([1.5, 0.3, 0.1])
            sphere(r=tailWidth/2, $fn=32);

        // Fluke tip
        translate([-bodyLength/2 - 15, side * tailWidth/2, -3])
            rotate([0, 0, side * 30])
            scale([0.8, 0.2, 0.05])
            sphere(r=tailWidth/3, $fn=32);
    }
}

module dorsalFin() {
    // Curved dorsal fin with 45° max overhang
    translate([-bodyLength/4, 0, bodyHeight/2])
        rotate([0, 15, 0])
        linear_extrude(height=finLength, twist=30, $fn=32)
            scale([0.5, 1, 1])
            circle(r=finWidth/2, $fn=32);
}

module pectoralFins() {
    // Pectoral fins on both sides
    for(side = [-1, 1]) {
        translate([bodyLength/4, side * bodyWidth/2, -bodyHeight/4])
            rotate([0, side * 45, 0])
            scale([1.2, 0.3, 0.1])
            sphere(r=finWidth, $fn=32);

        // Fin tip
        translate([bodyLength/4 + 8, side * (bodyWidth/2 + 5), -bodyHeight/4])
            rotate([0, side * 30, -10])
            scale([0.8, 0.2, 0.05])
            sphere(r=finWidth/1.5, $fn=32);
    }
}

module blowhole() {
    // Two small blowholes
    for(i = [0, 1]) {
        translate([bodyLength/2 - 5, -blowholeRadius + i * blowholeRadius * 2, bodyHeight/2 - 1])
            sphere(r=blowholeRadius, $fn=16);
    }
}

module eyes() {
    // Eyes on both sides
    color("black")
    for(side = [-1, 1]) {
        translate([bodyLength/2 + headLength/2 - 5, side * headWidth/3, headWidth/4])
            sphere(r=eyeRadius, $fn=16);
    }
}

// Assembly
union() {
    whaleBody();
    whaleHead();
    whaleTail();

    // Ensure no overhangs >45°
    dorsalFin();
    pectoralFins();

    // Surface details
    blowhole();
    eyes();
