// Realistic 3D printable computer mouse
// Medium complexity (~110 lines)

// Main body parameters
mouse_length = 95;
mouse_width = 55;
mouse_height = 35;

// Button parameters
button_depth = 15;
button_gap = 2;
scroll_wheel_radius = 4;
scroll_wheel_height = 10;

// Cable parameters
cable_length = 40;
cable_radius = 3;

// Wall thickness for 3D printing
wall = 2;

module mouse_body() {
    // Main ergonomic shell
    difference() {
        // Outer shell - rounded top
        hull() {
            translate([0, 0, mouse_height/2])
                scale([1, 0.8, 0.6])
                    sphere(d=mouse_width);
            translate([mouse_length/3, 0, mouse_height/2])
                scale([1.2, 1, 0.7])
                    sphere(d=mouse_width*1.1);
        }

        // Hollow interior
        translate([0, 0, -mouse_height/2])
            cube([mouse_length-wall*2, mouse_width-wall*2, mouse_height+10], center=true);

        // Button cutouts
        translate([mouse_length/2-button_depth/2, 0, mouse_height/2])
            cube([button_depth+1, mouse_width/2, mouse_height], center=true);
    }
}

module mouse_buttons() {
    // Left button
    translate([mouse_length/2-button_depth/2, -mouse_width/4, mouse_height/2-button_gap])
        difference() {
            cube([button_depth, mouse_width/2.5, mouse_height/4], center=true);
            translate([0, 0, -mouse_height/8])
                cube([button_depth+1, mouse_width/2.5+1, mouse_height/8], center=true);
        }

    // Right button
    translate([mouse_length/2-button_depth/2, mouse_width/4, mouse_height/2-button_gap])
        difference() {
            cube([button_depth, mouse_width/2.5, mouse_height/4], center=true);
            translate([0, 0, -mouse_height/8])
                cube([button_depth+1, mouse_width/2.5+1, mouse_height/8], center=true);
        }
}

module scroll_wheel() {
    // Scroll wheel between buttons
    rotate([90, 0, 0])
        translate([mouse_length/2-button_depth/2, 0, mouse_height/2+2])
            difference() {
                cylinder(h=scroll_wheel_height, r=scroll_wheel_radius, center=true);
                // Grip texture
                for(i=[0:30:359])
                    rotate([0, 0, i])
                        translate([scroll_wheel_radius*0.9, 0, 0])
                            cube([scroll_wheel_radius*0.4, scroll_wheel_height*1.1, 0.5], center=true);
            }
}

module mouse_feet() {
    // PTFE feet for smooth gliding
    foot_length = 10;
    foot_width = 6;
    foot_height = 0.5;

    // Front foot
    translate([mouse_length/4, 0, -mouse_height/2+foot_height/2])
        cube([foot_length, foot_width, foot_height], center=true);

    // Rear feet
    translate([-mouse_length/3, -mouse_width/3, -mouse_height/2+foot_height/2])
        cube([foot_length*0.8, foot_width*0.8, foot_height], center=true);
    translate([-mouse_length/3, mouse_width/3, -mouse_height/2+foot_height/2])
        cube([foot_length*0.8, foot_width*0.8, foot_height], center=true);
}

module usb_cable() {
    // Flexible cable exiting rear
    translate([-mouse_length/2, 0, -mouse_height/4])
        rotate([0, -20, 0])
            hull() {
                sphere(r=cable_radius);
                translate([-cable_length, 0, 0])
                    sphere(r=cable_radius*0.8);
            }
}

module sensor_window() {
    // Optical sensor window on bottom
    translate([0, 0, -mouse_height/2+0.1])
        cylinder(h=0.5, r=5);
}

// Assemble the mouse
union() {
    mouse_body();
    mouse_buttons();
    scroll_wheel();
    mouse_feet();
    usb_cable();
    sensor_window();
}

