// Realistic 3D Printable Mosquito
// Overall length ~70 mm

$fa = 2;
$fs = 0.5;

// Main module
module mosquito() {
    // Body orientation: head at +X, tail at -X
    color("darkslategray")
    union() {
        head();
        thorax();
        abdomen();
        wings();
        legs();
        proboscis();
    }
}

module head() {
    translate([28,0,0])
    rotate([0,-15,0])  // Slight downward tilt
    difference() {
        sphere(d=6);  // Main head sphere

        // Eyes (compound) - indentations
        translate([0,2.8,1.2])
        sphere(d=3.5);
        translate([0,-2.8,1.2])
        sphere(d=3.5);
    }
}

module thorax() {
    translate([15,0,2])
    scale([1.6,1,1.2])
    sphere(d=8);
}

module abdomen() {
    // Segmented abdomen
    for(i=[0:5]) {
        translate([10-i*3.5, 0, 1-i*0.3])
        scale([1-i*0.08, 1-i*0.08, 0.8-i*0.05])
        sphere(d=7-i);
    }
}

module proboscis() {
    // Main feeding tube
    translate([31,0,0])
    rotate([0,-30,0])
    cylinder(h=15, d1=1.5, d2=0.5, $fn=8);

    // Labium sheath
    translate([31,0,-0.8])
    rotate([0,-30,0])
    cylinder(h=14, d1=2, d2=1, $fn=8);
}

module wings() {
    // Left wing
    translate([15,4,5])
    rotate([0,10,-15])
    scale([1,2.5,0.3])
    wing_shape();

    // Right wing
    mirror([0,1,0])
    translate([15,4,5])
    rotate([0,10,15])
    scale([1,2.5,0.3])
    wing_shape();
}

module wing_shape() {
    difference() {
        union() {
            // Main wing blade
            hull() {
                translate([0,0,0])
                sphere(d=1);
                translate([5,12,0])
                sphere(d=0.5);
                translate([-2,18,0])
                sphere(d=0.3);
            }

            // Wing base
            sphere(d=2);
        }

        // Wing veins (negative space)
        for(i=[1:3]) {
            translate([0,i*4,0])
            rotate([0,0,i*5])
            scale([1,0.1,0.2])
            cube([6,1,1]);
        }
    }
}

module legs() {
    // Front legs
    for(side=[-1,1]) {
        // Front pair
        translate([20,side*2.5,1])
        rotate([15,0,side*30])
        leg_segment(8);

        // Middle pair
        translate([15,side*3,0])
        rotate([0,0,side*45])
        leg_segment(10);

        // Hind pair
        translate([10,side*2.5,-1])
        rotate([-15,0,side*60])
        leg_segment(12);
    }
}

module leg_segment(length) {
