    translate([0,0,length*0.4])
    rotate([30,0,0])
    cylinder(h=length*0.35, d=0.7, $fn=6);

    // Lower segment
    translate([0,0,length*0.4])
    translate([0,0,length*0.35])
    rotate([45,0,0])
    cylinder(h=length*0.25, d=0.5, $fn=6);

    // Foot
    translate([0,0,length*0.4])
    translate([0,0,length*0.35])
    translate([0,0,length*0.25])
    rotate([60,0,0])
    cylinder(h=1, d=0.4, $fn=6);
