    // Tail
    translate([0, -22, 8]) tail();
}

module body() {
    difference() {
        // Main torso
        hull() {
            translate([0, 0, 12]) scale([1.2, 1.5, 1]) sphere(10);
            translate([0, -10, 12]) scale([1, 1.2, 0.8]) sphere(8);
        }

        // Undercut for printing support
        translate([0, 0, -2]) cube([30, 40, 4], center=true);
    }
}

module head_and_neck() {
    union() {
        // Neck
        translate([0, 8, 0]) rotate([-20, 0, 0]) 
            scale([0.6, 0.6, 1.5]) cylinder(10, 5, 4);

        // Head
        translate([0, 15, 5]) rotate([30, 0, 0]) {
            // Main head shape
            scale([1.2, 1.5, 1]) sphere(6);

            // Snout
            translate([0, 5, -1]) scale([0.6, 0.8, 0.5]) sphere(5);

            // Ears
