// Nurse Figure - Realistic Medium Complexity
// Height: 80mm, built for 3D printing

// Parameters
body_height = 40;
head_radius = 8;
arm_length = 20;
leg_length = 25;
nurse_hat_height = 6;
nurse_hat_radius = 9;

// Colors for preview
color_skin = "#FFD1A4";
color_uniform = "#FFFFFF";
color_shoes = "#000000";
color_hair = "#8B4513";

// Main module
module nurse() {
    union() {
        // Head
        translate([0, 0, body_height + head_radius]) {
            head();
        }

        // Body
        translate([0, 0, leg_length]) {
            body();
        }

        // Legs
        legs();

        // Arms
        arms();

        // Nurse hat
        translate([0, 0, body_height + head_radius * 2]) {
            nurse_hat();
        }
    }
}

// Head with hair
module head() {
    color(color_skin) {
        sphere(r = head_radius, $fn = 32);
    }

    // Hair
    color(color_hair) {
        difference() {
            sphere(r = head_radius + 1, $fn = 32);
            translate([0, 0, -head_radius - 2])
                cube([head_radius * 2 + 2, head_radius * 2 + 2, head_radius * 2 + 4], center = true);
        }
    }

    // Eyes
    color("black") {
        translate([-3, -head_radius + 1.5, 2]) sphere(r = 0.8, $fn = 8);
        translate([3, -head_radius + 1.5, 2]) sphere(r = 0.8, $fn = 8);
    }

    // Nose
    color(color_skin) {
        translate([0, -head_radius + 0.5, 0.5]) sphere(r = 1, $fn = 8);
    }

    // Mouth
    color("red") {
        translate([0, -head_radius + 0.5, -2]) cylinder(h = 0.5, r1 = 1.5, r2 = 1.5, $fn = 16);
    }
}

// Nurse hat
module nurse_hat() {
    color(color_uniform) {
        // Main hat
        cylinder(h = nurse_hat_height, r1 = nurse_hat_radius, r2 = nurse_hat_radius * 0.9, $fn = 32);

        // Brim
        translate([0, 0, -1])
            cylinder(h = 2, r1 = nurse_hat_radius + 3, r2 = nurse_hat_radius + 1, $fn = 32);

        // Red cross
        color("red") {
            translate([0, 0, nurse_hat_height / 2 + 0.5]) {
                union() {
                    cube([6, 2, nurse_hat_height], center = true);
                    cube([2, 6, nurse_hat_height], center = true);
                }
            }
        }
    }
}

// Body with uniform
module body() {
    color(color_uniform) {
        // Torso
        union() {
            // Main body
            cylinder(h = body_height, r1 = 10, r2 = 8, $fn = 32);

            // Collar
            translate([0, 0, body_height - 2])
                cylinder(h = 2, r1 = 10.5, r2 = 9, $fn = 32);
        }

        // Arms connection points
        translate([-11, 0, body_height - 10])
            rotate([0, 90, 0])
                cylinder(h = 3, r = 3, $fn = 16);
        translate([11, 0, body_height - 10])
            rotate([0, 90, 0])
                cylinder(h = 3, r = 3, $fn = 16);
    }
}

// Arms
module arms() {
    // Left arm
    translate([-11, 0, leg_length + body_height - 10]) {
        rotate([0, 0, -30]) {
            color(color_uniform) {
                cylinder(h = arm_length, r = 2.5, $fn = 16);
            }
            translate([0, 0, arm_length - 2.5]) {
                color(color_skin) {
                    sphere(r = 2.5, $fn = 16);
                }
            }
        }
    }

    // Right arm
    translate([11, 0, leg_length + body_height - 10]) {
        rotate([0, 0, 30]) {
            color(color_uniform) {
                cylinder(h = arm_length, r = 2.5, $fn = 16);
            }
            translate([0, 0, arm_length - 2.5]) {
                color(color_skin) {
                    sphere(r = 2.5, $fn = 16);
                }
            }
        }
    }
}

// Legs and shoes
module legs() {
    // Left leg
    translate([-4, 0, 0]) {
        color(color_uniform) {
            cylinder(h = leg_length, r = 3, $fn = 16);
        }
        // Shoe
        translate([0, 0, -3]) {
            color(color_shoes) {
                union() {
                    cylinder(h = 6, r1 = 4, r2 = 3, $fn = 16);
                    translate([2, 0, 3])
                        cube([4, 3, 2], center = true);
                }
            }
        }
    }

    // Right leg
    translate([4, 0, 0]) {
        color(color_uniform) {
            cylinder(h = leg_length, r = 3, $fn = 16);
        }
        // Shoe
        translate([0, 0, -3]) {
            color(color_shoes) {
                union() {
                    cylinder(h = 6, r1 = 4, r2 = 3, $fn = 16);
                    translate([2, 0, 3])
                        cube([4, 3, 2], center = true);
                }
            }
        }
    }
}

// Create the nurse
nurse();

