// Nuthatch 3D Model (Medium Complexity - Realistic Style)
// Size: ~70mm length, 3mm wall thickness, 3D printable

$fn = 32;

// Main body module
module nuthatch_body() {
    // Main torso - elongated sphere
    translate([0, 0, 15]) 
        scale([1.3, 1, 1.2]) 
            sphere(15);

    // Back - slightly flattened top
    translate([0, 0, 25]) 
        scale([1.2, 0.9, 0.5]) 
            sphere(12);

    // Chest bulge
    translate([0, 8, 10]) 
        scale([0.8, 0.6, 1]) 
            sphere(12);
}

// Head module with beak
module nuthatch_head() {
    // Main head - rotated sphere
    translate([-10, 0, 30]) 
        rotate([0, 20, 0]) 
            scale([0.9, 1, 1.1]) 
                sphere(12);

    // Beak - cone
    translate([-22, 0, 32]) 
        rotate([0, -20, 0]) 
            cylinder(h=15, r1=2, r2=0.5);

    // Head cap (darker color area)
    translate([-12, 0, 38]) 
        scale([0.9, 1, 0.3]) 
            sphere(8);
}

// Wing modules
module wing(side) {
    mirror([side, 0, 0]) {
        // Main wing
        translate([5, -12, 22]) 
            rotate([0, -10, 20]) 
                scale([0.8, 0.3, 2]) 
                    sphere(10);

        // Wing tip
        translate([8, -15, 18]) 
            rotate([0, 0, 30]) 
                scale([0.5, 0.2, 1]) 
                    sphere(8);

        // Wing feathers - loop for detail
        for(i = [0:2]) {
            translate([6 + i*2, -15 - i*1.5, 20 - i*3]) 
                rotate([0, 0, 15 + i*10]) 
                    scale([0.4, 0.1, 0.8]) 
                        cube([6, 2, 1]);
        }
    }
}

// Tail module
module tail() {
    // Tail base
    translate([18, 0, 15]) 
        rotate([0, 30, 0]) 
            scale([0.5, 0.2, 1.5]) 
                sphere(8);

    // Tail feathers
    for(i = [-1:1]) {
        translate([22, i*3, 15 + i*2]) 
            rotate([0, 20 - abs(i)*5, i*10]) 
                scale([0.3, 0.1, 1.2]) 
                    cube([8, 2, 1]);
    }
}

// Leg and foot module
module foot(side) {
    mirror([side, 0, 0]) {
        // Leg
        translate([5, side*5, 5]) 
            cylinder(h=8, r=1.5);

        // Feet - three toes forward, one back
        // Forward toes
        for(i = [0:2]) {
            angle = -30 + i*30;
            translate([5, side*5, 0]) 
                rotate([0, 0, angle]) 
                    translate([0, 3, 0]) 
                        scale([0.3, 2, 0.3]) 
                            cube([1, 3, 1]);
        }

        // Back toe
        translate([5, side*5, 0]) 
            rotate([0, 0, 180]) 
                translate([0, 2, 0]) 
                    scale([0.3, 1.5, 0.3]) 
                        cube([1, 2, 1]);
    }
}

// Eye module
module eye(side) {
    mirror([side, 0, 0]) {
        // Eye socket
        translate([-12, side*5, 33]) 
            sphere(2);

        // Eye proper
        translate([-11.5, side*5, 33.5]) 
            sphere(1.5);

        // Eye highlight
        translate([-11, side*4.7, 34]) 
            sphere(0.5);
    }
}

// Main assembly
union() {
    // Body parts
    nuthatch_body();
    nuthatch_head();

    // Wings
    wing(1);
    wing(-1);

    // Tail
    tail();

    // Legs and feet
    foot(1);
    foot(-1);

    // Eyes
    eye(1);
    eye(-1);
}

