// Nutria - Realistic Style, Medium Complexity
// Overall dimensions: ~80mm long, 35mm wide, 35mm tall

$fn = 32;  // Smooth curves for 3D printing

// Main body assembly
module nutria() {
    // Body - elongated oval
    scale([1.4, 1, 0.8])
        sphere(r = 15, center = true);

    // Head
    translate([20, 0, 5])
        scale([1.2, 0.9, 0.9])
            sphere(r = 10, center = true);

    // Snout
    translate([32, 0, 3])
        scale([1.5, 0.7, 0.7])
            sphere(r = 6, center = true);

    // Ears (rounded triangles)
    for(side = [-1, 1]) {
        translate([18, side * 8, 12]) {
            rotate([0, side * 15, 0])
                scale([0.5, 1, 1.2])
                    cylinder(h = 6, r1 = 4, r2 = 2, center = true);
        }
    }

    // Eyes
    for(side = [-1, 1]) {
        translate([28, side * 6, 8]) {
            difference() {
                sphere(r = 2.5, center = true);
                translate([1, 0, 0])
                    sphere(r = 1.5, center = true);
            }
        }
    }

    // Whiskers
    for(side = [-1, 1]) {
        for(i = [-2:2]) {
            translate([36, side * (4 + i * 1.5), 3]) {
                rotate([0, 0, side * (-20 + i * 15)])
                    scale([1, 0.1, 0.1])
                        cylinder(h = 8, r = 1, center = true);
            }
        }
    }

    // Legs
    for(side = [-1, 1]) {
        // Front legs
        translate([10, side * 12, -8]) {
            rotate([10, 0, side * 10])
                scale([0.3, 0.3, 1])
                    cylinder(h = 18, r = 6, center = true);
        }

        // Rear legs
        translate([-15, side * 10, -6]) {
            rotate([10, 0, side * -15])
                scale([0.4, 0.4, 1.2])
                    cylinder(h = 20, r = 7, center = true);
        }

        // Webbed feet
        translate([10, side * 15, -16]) {
            scale([1.5, 2, 0.4])
                sphere(r = 4, center = true);

            // Toes
            for(toe = [-1, 0, 1]) {
                translate([toe * 3, side * 4, 0])
                    scale([0.6, 0.3, 0.2])
                        cylinder(h = 6, r = 2, center = true);
            }
        }

        translate([-15, side * 13, -16]) {
            scale([1.8, 2.2, 0.4])
                sphere(r = 5, center = true);

            for(toe = [-1, 0, 1]) {
                translate([toe * 3.5, side * 5, 0])
                    scale([0.7, 0.3, 0.2])
                        cylinder(h = 7, r = 2.5, center = true);
            }
        }
    }

    // Tail (thick and rounded)
    translate([-25, 0, -3]) {
        rotate([0, -20, 0])
            scale([1, 1, 0.6])
                cylinder(h = 30, r1 = 8, r2 = 5, center = true);
    }

    // Fur texture - small raised bumps
    for(i = [0:10]) {
        for(j = [0:5]) {
            a = i * 36;
            h = j * 3 - 7.5;
            translate([sin(a) * (12 + j * 2), cos(a) * (12 + j * 2), h]) {
                sphere(r = 1, center = true);
            }
        }
    }
}

// Generate the model
nutria();

