// Size: 80mm length, 40mm height, 20mm width

$fn = 32;

module body() {
    difference() {
        union() {
            // Main torso
            translate([0, 0, 15])
                scale([1.2, 0.6, 0.8])
                sphere(15);

            // Abdomen
            translate([-15, 0, 10])
                scale([1.5, 0.7, 0.6])
                sphere(10);

            // Chest
            translate([15, 0, 18])
                scale([1.3, 0.8, 0.9])
                sphere(12);
        }

        // Remove bottom for flat base
        translate([0, 0, -5])
            cube([100, 100, 10]);
    }
}

module head() {
    translate([30, 0, 25]) {
        rotate([0, -10, 0]) {
            // Skull
            scale([1, 0.6, 0.8])
                sphere(8);

            // Muzzle
            translate([8, 0, -2])
                scale([1.2, 0.7, 0.6])
                sphere(6);

            // Eyes
            translate([8, -3, 2])
                sphere(1.5);
            translate([8, 3, 2])
                sphere(1.5);

            // Ears
            translate([0, -4, 5])
                rotate([0, -20, 0])
                cylinder(6, 2, 1);
            translate([0, 4, 5])
                rotate([0, -20, 0])
                cylinder(6, 2, 1);
        }
    }
}

module legs() {
    // Front legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([15, side * 8, 15])
            rotate([0, side * 10, 0])
            cylinder(15, 3, 2.5);

        // Lower leg
        translate([15, side * 9, 0])
            cylinder(12, 2.5, 2);

        // Hoof
        translate([15, side * 9, -2])
            cylinder(3, 2.2, 2);
    }

    // Rear legs
    for(side = [-1, 1]) {
        // Upper leg
        translate([-15, side * 7, 12])
            rotate([0, side * -15, 0])
            cylinder(12, 3.5, 3);

        // Lower leg
        translate([-15, side * 7.5, 0])
            cylinder(10, 3, 2.5);

        // Hoof
        translate([-15, side * 7.5, -2])
            cylinder(3, 2.7, 2.5);
    }
}

module neck() {
    translate([15, 0, 20]) {
        rotate([0, 30, 0])
            cylinder(15, 4, 3);
    }
}

module tail() {
    translate([-25, 0, 15]) {
        rotate([30, 0, 0])
            cylinder(15, 2, 1);
    }
}

module stripes() {
    // White stripes on torso
    for(i = [1, 3, 5, 7, 9]) {
        translate([20 - i * 3, 0, 18])
            scale([0.8, 0.3, 0.05])
            sphere(8);
    }

    for(i = [2, 4, 6, 8]) {
        translate([20 - i * 3, 0, 15])
            scale([0.8, 0.3, 0.05])
            sphere(8);
    }

    // White stripes on legs
    for(side = [-1, 1]) {
        translate([15, side * 8.5, 8])
            rotate([0, 90, 0])
            cylinder(3, 1, 1);

        translate([-15, side * 7, 7])
            rotate([0, 90, 0])
            cylinder(3, 1.5, 1.5);
    }
}

// Main assembly
union() {
    body();
    neck();
    head();
    legs();
    tail();
    stripes();
}

