// Realistic newt model - medium complexity
// Overall size ~70mm length

$fn = 32;

// Main body module
module newt_body() {
    // Torso - elongated sphere
    translate([0, 0, 8])
    scale([1.8, 1, 1])
    sphere(12);

    // Head - slightly flattened sphere
    translate([25, 0, 10])
    scale([1.2, 0.9, 0.7])
    sphere(8);

    // Snout - cone
    translate([33, 0, 10])
    rotate([0, 90, 0])
    cylinder(h=8, r1=4, r2=0);
}

// Limb module
module limb(length, width, angle) {
    rotate([0, 0, angle])
    translate([length/2, 0, 0])
    union() {
        // Upper segment
        translate([0, 0, 0])
        rotate([0, 90, 0])
        cylinder(h=length/2, r=width);

        // Lower segment
        translate([length/2, 0, -width])
        rotate([30, 90, 0])
        cylinder(h=length/2, r=width*0.8);

        // Hand/foot
        translate([length, 0, -width*1.5])
        sphere(width*1.2);
    }
}

// Tail module
module tail() {
    hull() {
        translate([-20, 0, 8])
        sphere(6);

        translate([-35, 0, 6])
        scale([1, 0.7, 0.5])
        sphere(4);

        translate([-45, 0, 5])
        scale([1, 0.5, 0.3])
        sphere(2);
    }
}

// Eye module
module eye(position) {
    translate(position)
    union() {
        // Eyeball
        sphere(1.5);

        // Pupil
        translate([0.5, 0, 0])
        color("black")
        sphere(0.8);
    }
}

// Skin texture - small bumps
module skin_bumps() {
    for(i = [-20:5:20]) {
        for(j = [-10:5:10]) {
            if((i*i + j*j) < 400) {
                translate([i, j, 12 + 12*cos(i/10)*sin(j/10)])
                sphere(0.5);
            }
        }
    }
}

// Main assembly
union() {
    // Body
    newt_body();

    // Tail
    tail();

    // Front limbs
    limb(12, 2, 30);
    mirror([0, 1, 0])
    limb(12, 2, 30);

    // Rear limbs
    translate([-15, 0, 0])
    limb(15, 2.5, -30);
    translate([-15, 0, 0])
    mirror([0, 1, 0])
    limb(15, 2.5, -30);

    // Eyes
    color("darkolivegreen")
    eye([30, 3, 12]);
    color("darkolivegreen")
    eye([30, -3, 12]);

    // Skin texture
    color("darkolivegreen")
    skin_bumps();
}

// Base for stability
translate([0, 0, -2])
color("darkolivegreen")
cylinder(h=2, r=25);

