// Okapi - Realistic style, medium complexity
// All dimensions in mm, printable at 100 mm height

$fn=32;

// Overall scale
scale_factor = 100/120;

// Main assembly
scale([scale_factor,scale_factor,scale_factor]) {

    // Body
    union() {
        // Torso
        translate([0,0,45]) {
            scale([1.2,0.7,0.7]) sphere(r=30);
        }

        // Neck base
        translate([40,0,65]) {
            rotate([0,-15,0]) 
            scale([0.8,0.6,1.5]) cylinder(r1=15,r2=8,h=40);
        }
    }

    // Head
    module head() {
        translate([70,0,75]) {
            rotate([0,-15,0]) {
                // Skull
                scale([1.3,0.7,0.8]) sphere(r=15);

                // Muzzle
                translate([15,0,-5]) {
                    rotate([0,-10,0]) 
                    scale([1.2,0.5,0.5]) cylinder(r=8,h=15);
                }

                // Ears
                translate([-5,10,8]) {
                    rotate([0,0,-20]) 
                    scale([0.3,0.1,0.8]) cylinder(r=8,h=15);
                }
                translate([-5,-10,8]) {
                    rotate([0,0,20]) 
                    scale([0.3,0.1,0.8]) cylinder(r=8,h=15);
                }

                // Horns
                translate([5,4,12]) cylinder(r1=2,r2=0.5,h=8);
                translate([5,-4,12]) cylinder(r1=2,r2=0.5,h=8);
            }
        }
    }
    head();

    // Legs
    module leg(x,y,z,angle) {
        translate([x,y,z]) {
            rotate([angle,0,0]) {
                // Upper leg
                cylinder(r1=8,r2=6,h=40);

                // Lower leg
                translate([0,0,40]) {
                    rotate([5,0,0]) 
                    cylinder(r1=6,r2=4,h=35);
                }

                // Hoof
                translate([0,0,75]) {
                    scale([1.2,1,0.6]) 
                    cylinder(r=4,h=8);
                }
            }
        }
    }

    // Front legs
    leg(30,15,30,-20);
    leg(30,-15,30,-20);

    // Rear legs
    leg(-30,15,30,-20);
    leg(-30,-15,30,-20);

    // Tail
    translate([-40,0,55]) {
        rotate([0,0,30]) {
            scale([0.4,0.4,1]) cylinder(r=5,h=30);
        }
    }

    // Stripes on legs
    module stripes() {
        for(i=[1:3]) {
            // Front left
            translate([30,15,45-i*8]) {
                rotate([90,0,0]) 
                difference() {
                    cylinder(r=8.5,h=5);
                    cylinder(r=7.5,h=5.1);
                }
            }

            // Front right
            translate([30,-15,45-i*8]) {
                rotate([90,0,0]) 
                difference() {
                    cylinder(r=8.5,h=5);
                    cylinder(r=7.5,h=5.1);
                }
            }
        }
    }
